import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const y=JSON.parse('{"title":"广东白云学院","description":"","frontmatter":{"title":"广东白云学院","author":["canyie"],"wordCount":1171},"headers":[],"relativePath":"campus/BaiyunU.md","filePath":"campus/BaiyunU.md","lastUpdated":1739159899000}'),n={name:"campus/BaiyunU.md"};function d(i,t,s,c,o,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.baiyunu.edu.cn/" target="_blank" rel="noreferrer">广东白云学院</a>（Guangdong Baiyun University, BaiyunU）。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校现有 2+1 个校区，按专业分配校区。</p><ul><li>西校区位于<a href="https://amap.com/place/B00140TVGJ" target="_blank" rel="noreferrer">广州市白云区学苑路 1 号</a>，机电工程学院、大数据学院、电气学院本科生在此校区。设施较老旧，距离市区及地铁站较远但周边有小吃一条街之类的，坐地铁需要打车 / 乘坐公交车 / 乘坐校门口的便民车到最近的滘心地铁站，由于道路较窄等原因，周五下午等高峰时段很容易堵在路上，若看见出行软件提示堵车建议打车并要求司机走高速到亭岗地铁站以绕开拥挤路段。广州地铁 8 号线北延段预计在 2028 年开通，届时会有更近的江高地铁站。</li><li>北校区位于<a href="https://amap.com/place/B0FFJK1BNH" target="_blank" rel="noreferrer">广州市白云区九佛西路 280 号</a>，除以上三个二级学院外其他所有本科生在此校区。较偏僻，学校外面基本没什么，距离市区及其他校区较远，距离地铁站较近（最近的地铁站为红卫地铁站）。</li><li>隐藏的东校区位于<a href="https://amap.com/place/B0FFGLMQ3K" target="_blank" rel="noreferrer">广州市白云区江高镇田南路 13 号</a>，主要为技师学院学生，西校区特殊活动可能会在这里举办。离西校区近，步行可在五分钟内抵达西校区，故不再赘述。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>大一军训约为两周时间，于 10 月份国庆节假期结束后开始，笔者因患有精神类疾病及曾患心肌炎申请了免训，不清楚具体情况，但据同学反映本校军训强度不高。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>大一上学期体育课按班级上，大一下学期开始选课。大一上学期的体育课似乎按二级学院区分具体上课内容，笔者在大数据学院，上课内容为五步拳等，强度不算高但部分动作对有肢体震颤的同学可能难以完成。五步拳似乎是为了之后在校运会上进行表演，是否参演为自愿，但表演时会播放《男儿当自强》作伴奏，可能触发性别焦虑。</p><p>不要求校园跑。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测按《国家学生体质健康标准》执行，笔者因患有精神类疾病及曾患心肌炎申请了免测。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>不清楚东校区情况。北校区全部为上床下桌四人间，西校区上下床八人间和上床下桌六人间都有，上床下桌六人间为 2023 年改造出来的，目前主要供 2023 级大一新生居住，不清楚之后是否会继续改造。两个校区宿舍都有独立卫生间和浴室，西校区有单人间（住宿费 10000 / 年）及双人间（住宿费 7000 / 年）。西校区有部分宿舍楼男女混住（男生住低层女生住高层），可以一定程度上缓解 “女生从男生宿舍楼出来” 的尴尬。不清楚是否可以自己租房住，但听说有学生在校外民宿长期租住。</p><p>西校区设施普遍较老旧，目前没有在西校区内发现过第三卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>不清楚校医院情况。西校区离白云区第二人民医院（三甲医院）很近（高塘门诊部 1.9 公里，总院 2.8 公里），离广州医科大学附属脑科医院（三甲医院）白云院区只有四公里，可以尝试在此处开证，但该院区周末不开诊，周末需要去芳村总院。进入市区后可以尝试在 广州医科大学附属脑科医院 / 暨南大学附属第一医院 等医院开证。</p>',19)]))}const p=e(n,[["render",d]]);export{y as __pageData,p as default};
