import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"中国农业大学","description":"","frontmatter":{"title":"中国农业大学","author":["徐小飞(XuFloat)"],"wordCount":1005},"headers":[],"relativePath":"campus/CAU.md","filePath":"campus/CAU.md","lastUpdated":1739159899000}'),n={name:"campus/CAU.md"};function d(i,t,o,h,c,s){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✗</td><td style="text-align:center;">个别女生宿舍除外</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;">个别女生宿舍除外</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✗</td><td style="text-align:center;">合同医院公费医疗</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.cau.edu.cn/" target="_blank" rel="noreferrer">中国农业大学</a>（China Agricultural University, CAU），中国农业大学作为教育部直属高校，是我国现代农业高等教育的起源地，其历史起自于 1905 年成立的京师大学堂农科大学。位列 “双一流”、“985 工程”、“211 工程”.</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>中国农业大学东校区 位于<a href="https://ditu.amap.com/place/B000A8129P" target="_blank" rel="noreferrer">北京市海淀区清华东路 17 号</a>，新生将入学本校区，一部分学生将于大二转去西校区，设施齐全，有教学楼、学科楼、男女生公寓、研究生公寓、商业街（店铺较少）、新图书馆、奥运体育馆、食堂（多达 6 个），等。</p></li><li><p>中国农业大学西校区 位于<a href="https://ditu.amap.com/place/B000A88E6K" target="_blank" rel="noreferrer">北京市海淀区圆明园西路 2 号</a>，西校区是科研中心，笔者尚未转去西校区，了解不多。</p></li></ul><p>东西校区间有定时班车来往。</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>2023 年军训时长 2 周，军训无头发需求，军训强度不高，训练一段时间会集体休息，军训可以申请免训。军训包含一次夜间拉练，长度为 16km，会要求打背包（即把被子背上），速度为快步走，次日休息。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>新生需要通过《身体运动功能》课程，才能选择其他体育课程。</p><p>《身体运动功能》课程可以《康复课程》代替，申请需要三甲医院证明，详细参见中国农业大学体育部<a href="http://tyb1.cau.edu.cn/art/2022/8/29/art_47855_899523.html" target="_blank" rel="noreferrer"> 2022-2023 学年秋季学期体育课选课说明</a> -<strong> 四、保健康复课的选课条件及流程</strong></p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测按照 中国农业大学体育部<a href="http://tyb1.cau.edu.cn/col/col48262/index.html" target="_blank" rel="noreferrer">本科生体育课程体系介绍</a> - 考核体系介绍进行。</p><p>体测可在有三甲医院证明的情况下申请免测。</p><p>详见:</p><ul><li>中国农业大学体育部<a href="http://tyb1.cau.edu.cn/col/col48262/index.html" target="_blank" rel="noreferrer">本科生体育课程体系介绍</a></li><li>中国农业大学体育部<a href="http://tyb1.cau.edu.cn/art/2022/8/29/art_47855_899523.html" target="_blank" rel="noreferrer"> 2022-2023 学年秋季学期体育课选课说明</a></li></ul><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><h3 id="东校区宿舍" tabindex="-1">东校区宿舍 <a class="header-anchor" href="#东校区宿舍" aria-label="Permalink to &quot;东校区宿舍&quot;">​</a></h3><p>男生宿舍全部为 6 人寝室，上床下桌，有空调，每层楼有水房卫生间若干，浴室位于宿舍一层（无隔间）。</p><p>女生宿舍一部分为 6 人寝室（与男生寝室相同）与男生寝室相同，一部分为 4 人寝室有独立卫浴。</p><p>研究生楼下有男女生浴室，有隔间，但是距离男生宿舍路途较远。</p><h3 id="西校区宿舍" tabindex="-1">西校区宿舍 <a class="header-anchor" href="#西校区宿舍" aria-label="Permalink to &quot;西校区宿舍&quot;">​</a></h3><p>为 6 人寝室。浴室为独栋，23 年秋刚刚翻新，有隔间。</p><p>本校不查寝不封寝，可选择校外居住。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院工作日工作时间门诊开放。急诊有值班医生。</p><p>校医院就诊全程一折，校医院购买药物也为一折。</p><p>去医院看病需要开具转诊单方可报销。</p><p>每月固定时间按报销公费医疗。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>并未有明显分歧。（笔者入学时长较短）</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>未知（笔者入学时长较短）</p><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><p>如果被班主任老师知道有重度精神问题，可能会直接叫家长陪读，不得住宿舍。</p><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="徐小飞" tabindex="-1">徐小飞 <a class="header-anchor" href="#徐小飞" aria-label="Permalink to &quot;徐小飞&quot;">​</a></h3><ul><li>QQ：3519412371</li><li>E-Mail：<a href="mailto:xufloat@outlook.com" target="_blank" rel="noreferrer">xufloat@outlook.com</a></li></ul>',40)]))}const x=e(n,[["render",d]]);export{u as __pageData,x as default};
