import{_ as t,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const n="/assets/CSU_BILL.PwH9W3QI.jpg",f=JSON.parse('{"title":"中南大学","description":"","frontmatter":{"title":"中南大学","author":["AlbacoreMeow","Cryolitia"],"wordCount":1644},"headers":[],"relativePath":"campus/CSU.md","filePath":"campus/CSU.md","lastUpdated":1729083604000}'),o={name:"campus/CSU.md"};function i(p,e,c,h,s,d){return r(),a("div",null,e[0]||(e[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">部分</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">只能处理日常疾病🤔</td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.csu.edu.cn/" target="_blank" rel="noreferrer">中南大学</a>（Central South University, CSU）</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li>校本部（北校区）位于 <a href="https://amap.com/place/B02DB065XK" target="_blank" rel="noreferrer">长沙市岳麓区麓山南路 932 号</a></li><li>南校区位于 <a href="https://amap.com/place/B02DB02641" target="_blank" rel="noreferrer">长沙市岳麓区麓山南路 605 号</a>，位于校本部南侧麓山南路对面，门口有 <a href="https://amap.com/place/BV10783889" target="_blank" rel="noreferrer">中南大学 地铁站</a>。新生均在此住宿。</li><li>新校区位于 <a href="https://amap.com/place/B02DB0TJRN" target="_blank" rel="noreferrer">长沙市岳麓区潇湘中路</a>，位于南校区南侧清水路对面，门口有 <a href="https://amap.com/place/BV10783889" target="_blank" rel="noreferrer">中南大学 地铁站</a>。绝大部分新生在此上课。</li><li>铁道校区位于 <a href="https://amap.com/place/B02DB065XM" target="_blank" rel="noreferrer">长沙市天心区韶山南路 68 号</a>，门口有 <a href="https://amap.com/place/BV10230150" target="_blank" rel="noreferrer">铁道学院 地铁站</a>。</li><li>湘雅医学院旧校区位于 <a href="https://amap.com/place/B02DB10O89" target="_blank" rel="noreferrer">长沙市开福区湘雅路 88 号</a>，门口有 <a href="https://amap.com/place/BS11901261" target="_blank" rel="noreferrer">文昌阁 地铁站</a>。</li><li>湘雅医学院新校区位于 <a href="https://amap.com/place/B02DB02K3F" target="_blank" rel="noreferrer">长沙市岳麓区桐梓坡路 172 号</a>，门口有 <a href="https://amap.com/place/BV10854311" target="_blank" rel="noreferrer">湘雅三医院 地铁站</a>。</li></ul><p>以上各校区之间均有班车往来，班车时刻表可在微信公众号 中南大学信网中心 查询。</p><ul><li>中南大学湘雅医院位于 <a href="https://amap.com/place/B02DB00B1E" target="_blank" rel="noreferrer">长沙市开福区湘雅路 87 号</a>，紧邻湘雅医学院旧校区。</li><li>中南大学湘雅二医院位于 <a href="https://amap.com/place/B02DB02FOO" target="_blank" rel="noreferrer">长沙市芙蓉区人民中路 139 号</a>，门口有 <a href="https://amap.com/place/BS11901291" target="_blank" rel="noreferrer">窑岭湘雅二医院 地铁站</a>。</li><li>中南大学湘雅三医院位于 <a href="https://amap.com/place/B02DB02J64" target="_blank" rel="noreferrer">长沙市岳麓区桐梓坡路 138 号</a>，紧邻湘雅医学院新校区。</li><li>中南大学湘雅口腔医院位于 <a href="https://amap.com/place/B0FFMG6X4M" target="_blank" rel="noreferrer">长沙市开福区中南大学湘雅医学院西南 2 门旁</a>，紧邻湘雅医学院旧校区。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><blockquote><p>军训是本科国防生（还有自训营）学长学姐带，比较宽容。服饰要求统一军训服，头发没有要求。</p><p>身体不适可以随时要求休息，体检结果稍有异常（笔者只是心率 112）就可以申请跟训，会分配写通讯稿等较为轻松的任务。</p></blockquote><h3 id="体育" tabindex="-1">体育 <a class="header-anchor" href="#体育" aria-label="Permalink to &quot;体育&quot;">​</a></h3><p>大一、大二两个学期有体育课，体育课外长跑：</p><p>体育课自由选课，部分热门课程可能需要抢。开设传统体育养生课程，在选课阶段很难抢到，抢不到可以尝试先选需要剧烈运动的课程然后在开学后第一周以 HRT 的各种副作用为理由尝试向老师申请改选传统体育养生。</p><p>每学期都有体测，此时可尝试持易性症诊断证明向体育老师申请免测，决定权似乎在体育老师自己。</p><p>男性学籍要求在指定操场每学期 36 次长跑 3.5km；女性学籍为 36 次长跑 2.5km，配速 10 以内。占体育期末成绩 10%。</p><p>大三以及大四上学期（四年制）仅有体测：</p><p>此时申请免测需打印申请表并填写，辅导员签字后交至体育教研部。<s>如果同时手写一封「不参与保研评优承诺书」一并递交将省去许多麻烦。</s></p><blockquote><p><s>理论上不能骑自行车和修改定位而且偶尔会严查但实际上笔者没少这么干，近两年甚至有开无人机在操场绕圈的</s></p></blockquote><h3 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h3><p>新生第一年是在南校区升华公寓（也有倒霉学院会分到历史悠久的南 X 舍），根据楼号略有不同四人套间的十栋已经作为研究生宿舍，本科生比较大可能是二间共一个阳台 / 独立阳台的户型（均带独卫，但是带热水的淋浴间是每层共用一个） 第二 / 三学年根据专业决定是否搬校区，有住在本部岳麓山脚的桃花公寓（和南校接近）和铁道校区（情况较差，但是有几栋新建的公寓状况较好）</p><p>租房价格大概 700（合租）/1500（单间），校外租房原则上需父母同意且辅导员知情。</p><p>教学楼厕所基本都有隔间，新校区部分楼有无障碍厕所。</p><p>学校食堂消费尚可，学校周围饮食丰富。</p><h2 id="医疗资源情况" tabindex="-1">医疗资源情况 <a class="header-anchor" href="#医疗资源情况" aria-label="Permalink to &quot;医疗资源情况&quot;">​</a></h2><p>校本部职工医院门诊可凭三甲医院写有 HRT 方案的门诊病历或处方单拿雌二醇凝胶和补佳乐，<s>或者直接大摇大摆什么都不带非常自信的进去说给我来盒补佳乐诊断写易性症</s>。如正常参保大学生医保，校职工医院门诊药房个人自付比例为 30%，一年报销上限为 560 元。因药物供应原因，目前只有健民 40g 雌二醇凝胶，无爱斯妥雌二醇凝胶。</p><p><img src="'+n+'" alt="缴费单据"></p><p>校本部职工医院可测肝功能等基础项目，无法测激素六项等更复杂的血生化项目。</p><p>内科值班医生态度友好，有湘雅的病历会直接照着开药不会过多询问。</p><p>中南大学有三所附属三甲医院，提供精神科和内分泌科一条龙服务，但均距离校本部较远。</p><h2 id="交通" tabindex="-1">交通 <a class="header-anchor" href="#交通" aria-label="Permalink to &quot;交通&quot;">​</a></h2><p>大部分学院大一大二住宿在南校区而上课在新校区 A-D 座。两者之间约有 1.5km 的步行距离。步行上学时间大约在 30min 左右，校内有大量共享单车但是起晚了还是有可能抢不到。有连接两个校区的公交车，大约 1 元 2 站地。更为偏远的新校区部分学院楼和新校区北门之间有 1 元校车通勤，可以步行前往新校区北门乘坐校车。</p><h2 id="校园文化" tabindex="-1">校园文化 <a class="header-anchor" href="#校园文化" aria-label="Permalink to &quot;校园文化&quot;">​</a></h2><blockquote><p>辅导员两极差异很大，有的辅导员非常传统保守，有的辅导员甚至自己就是非二元性别完全理解支持性少数群体。 笔者尝试过数次裙装上课 / 参加活动，整体来看女性同学基本都很友好，部分男性存在恶意调侃 / 阴阳怪气。 学校整体<s>学习氛围浓厚</s>较为压抑，整体上可能更加注重卷面成绩和学习态度。</p></blockquote><h2 id="多元性别群体分布" tabindex="-1">多元性别群体分布 <a class="header-anchor" href="#多元性别群体分布" aria-label="Permalink to &quot;多元性别群体分布&quot;">​</a></h2><p>目前校内相关人数较少，且基本独自行动</p><h2 id="笔者联系方式" tabindex="-1">笔者联系方式 <a class="header-anchor" href="#笔者联系方式" aria-label="Permalink to &quot;笔者联系方式&quot;">​</a></h2><h3 id="cryolitia" tabindex="-1">Cryolitia <a class="header-anchor" href="#cryolitia" aria-label="Permalink to &quot;Cryolitia&quot;">​</a></h3><ul><li>Email: <a href="mailto:Cryolitia@gmail.com" target="_blank" rel="noreferrer">Cryolitia@gmail.com</a></li></ul>',38)]))}const b=t(o,[["render",i]]);export{f as __pageData,b as default};
