import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"成都信息工程大学","description":"","frontmatter":{"title":"成都信息工程大学","author":["Rosmontic"],"wordCount":665},"headers":[],"relativePath":"campus/CUIT.md","filePath":"campus/CUIT.md","lastUpdated":1739159899000}'),n={name:"campus/CUIT.md"};function i(d,t,o,s,c,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.cuit.edu.cn/" target="_blank" rel="noreferrer">成都信息工程大学</a> (Chengdu University of Information Technology), 成都信息工程大学是四川省人民政府和中国气象局、国家统计局共建、四川省重点发展的省属普通本科院校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>航空港校区位于<a href="https://www.amap.com/place/B001C0650L" target="_blank" rel="noreferrer">四川省 成都市 西南航空港经济开发区 学府路一段 24 号</a>，一般来说为理工类学科学生日常学习的校区，校门口有公交车站，可以坐公交去附近的顺风地铁站，也可以走着去（大约两公里多），从顺风到成都市中心（天府广场）大概半小时。</p></li><li><p>龙泉校区位于<a href="https://www.amap.com/place/B001C06VLJ" target="_blank" rel="noreferrer">成都市 龙泉驿区 阳光城 幸福路 10 号</a>，一般来说为文科类学生日常学习的校区，笔者对该校区的情况并不了解。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训不管头发，持有医疗证明可以免训，训练在上午和晚上进行，强度看教官个人，甚至存在有的教官管的很松你一天都不用去，但要注意关注及时交材料表格之类的文件</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>不分性别，体育课分数包含考勤 (20%) 体测 (20%) 校园跑 (10%) 期末考核 (50%)</p><p><s>不会真有人自己跑校园跑吧</s></p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>六人间，独立卫浴，两个上下铺 + 两个上床下桌，所以说三个人用一张床的桌子，非常挤。</p><p>学校实际上不管校外居住 (理论上要申请走读，但是不管)，想出去住可以直接走，附近小区很多，大概 700 左右就能租到一个单间。</p><p>希望有同类来和笔者合租（</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>报销需先在校医院开转诊单，笔者没有尝试过</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>目前看几个技术类社团实验室友好气氛较好</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>笔者本人只认识毕业了的学姐（</p><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="rosmontic" tabindex="-1">Rosmontic <a class="header-anchor" href="#rosmontic" aria-label="Permalink to &quot;Rosmontic&quot;">​</a></h3><ul><li>Telegram:<a href="https://t.me/Rosmontis233" target="_blank" rel="noreferrer">https://t.me/Rosmontis233</a></li><li>Twitter:<a href="https://x.com/Rosmontis233" target="_blank" rel="noreferrer">https://x.com/Rosmontis233</a></li><li>E-Mail:<a href="mailto:rosmontis233@hotmail.com" target="_blank" rel="noreferrer">rosmontis233@hotmail.com</a></li></ul>',24)]))}const m=e(n,[["render",i]]);export{x as __pageData,m as default};
