import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"中国矿业大学","description":"","frontmatter":{"title":"中国矿业大学","author":["紫雪"],"wordCount":1239},"headers":[],"relativePath":"campus/CUMT.md","filePath":"campus/CUMT.md","lastUpdated":1728116039000}'),n={name:"campus/CUMT.md"};function o(d,t,i,c,h,s){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">-</td><td style="text-align:center;">取决于辅导员</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.cumt.edu.cn/" target="_blank" rel="noreferrer">中国矿业大学</a>（China University of Mining and Technology, CUMT），是教育部直属的全国重点大学，教育部与江苏省人民政府、应急管理部共建高校，国家 “双一流” 建设高校，国家 “211 工程”“985 工程优势学科创新平台” 建设高校，全国首批博士和硕士学位授予单位，高水平行业特色大学优质资源共享联盟、世界能源大学联盟和国际矿业、能源与环境高等教育联盟成员，入选 “111 计划”、卓越工程师教育培养计划、国家建设高水平大学公派研究生项目、中国政府奖学金来华留学生接收院校，国家大学生创新性实验计划、新工科研究与实践项目、全国高校实践育人创新创业基地。</p><blockquote><p>笔者补充：中流 211，今年在广东省招生分数线大概会在 600 分上下，性价比较高。</p></blockquote><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校有两个校区，分别为南湖校区和文昌校区。</p><p>南湖校区位于<a href="https://amap.com/place/B020402BCG" target="_blank" rel="noreferrer">徐州市铜山区大学路 1 号</a></p><p>文昌校区位于<a href="https://amap.com/place/B020400CEX" target="_blank" rel="noreferrer">徐州市泉山区金山东路 1 号</a>。</p><p>本科生来的都是南湖校区，研究生一般去的是文昌校区，两校区间有校园巴士直达，文昌位于市区，南湖紧挨着 5A 级景区云龙湖风景区。环境优美。目前南湖校区正在修建地铁，预计几年之后建成。文昌校区地铁开在校门口旁边。</p><p>南湖校区 63 路公交车直达市中心苏宁广场。</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>笔者所在的信息与控制工程学院军训男生要求 3mm 头发，但军训可以凭三甲医院的不适宜运动的诊断证明获得免训。</p><blockquote><p>军训时长不长难度不高，且不舒服时可以申请随训（在遮阳棚里看别人训）。</p></blockquote><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><blockquote><p>笔者所在的信息与控制工程学院导员对跨性别态度较为友善，凭诊断证明或者病历均可申请体测免测以及体育课改上保健课。</p></blockquote><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>同上</p><h4 id="运动世界跑步及一些健身锻炼" tabindex="-1">运动世界跑步及一些健身锻炼 <a class="header-anchor" href="#运动世界跑步及一些健身锻炼" aria-label="Permalink to &quot;运动世界跑步及一些健身锻炼&quot;">​</a></h4><p>同上，凭诊断证明或者病历均可申请免打卡。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>南湖校区宿舍情况没得说，全国排名前五。寝室环境好，有独立卫浴（两间宿舍打通且共用卫浴），应该可以申请调换寝室，但不可以单人居住，可以申请校外租房居住，学校南门对面就有很多的房间可以租，基础设施没啥问题。</p><p>学校各处没有第三卫生间。</p><p>详情请点击查看<a href="https://colleges.chat/universities/zhong-guo-kuang-ye-da-xue/" target="_blank" rel="noreferrer">中国矿业大学</a></p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院不可以开具激素检查，可以公费但不可以医保购买 HRT 药物。校外医院均可以检查和开具药物，但不能回校报销。前往校外医院很方便，南边 3 km 处就有一家大型三甲医院。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><blockquote><p>学校、教师和同学都对此没有什么反应（至少表面上是这样子），友善算不上，但是肯定没啥歧视，大家基本上都把目光聚焦在了顺直人的谈情说爱中。学校没有 LBGT 专门的社团组织，没看到有 LBGT 的科普，但是有几个 Les 和 Gay 的 QQ 群。学校心理咨询师大概不懂跨性别。</p></blockquote><h3 id="跨性别分别情况" tabindex="-1">跨性别分别情况 <a class="header-anchor" href="#跨性别分别情况" aria-label="Permalink to &quot;跨性别分别情况&quot;">​</a></h3><p>我们学校目前只有我一只 MtF (呜呜呜</p><blockquote><p>这个学校真的很不错，对 MtF 很包容的，笔者已经替大家探过路了，2023 届的跨跨快来呀～</p></blockquote><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><blockquote><p>各个院系的老师和同学都挺专注于科研和学业的，基本上没人关注 LGBT 相关议题。</p></blockquote><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><blockquote><p>中国矿业大学各方面都还算不错，可是这个大学在跨性别群体里基本没有知名度，笔者挺伤心的 QWQ</p></blockquote><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><p>紫雪：<a href="mailto:qpwoeqwe@163.com" target="_blank" rel="noreferrer">qpwoeqwe@163.com</a></p>',38)]))}const b=e(n,[["render",o]]);export{u as __pageData,b as default};
