import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"东华大学","description":"","frontmatter":{"title":"东华大学","author":"wakaba","wordCount":1358},"headers":[],"relativePath":"campus/DHU.md","filePath":"campus/DHU.md","lastUpdated":1739159899000}'),n={name:"campus/DHU.md"};function d(i,t,s,c,o,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✗</td><td style="text-align:center;">研究生新楼有</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;">研究生新楼有</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">-</td><td style="text-align:center;">部分楼有（如学院楼）</td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">-</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.dhu.edu.cn/" target="_blank" rel="noreferrer">东华大学</a>（Donghua University, DHU）是教育部直属、国家 “211 工程”、国家 “双一流” 建设高校。学校秉承 “崇德博学、砺志尚实” 的校训，不断开拓奋进，已发展成为以纺织、材料、设计为优势，特色鲜明的多科性、高水平大学。</p><p>学校总体氛围较为开放，男女比接近 1：1，延安路校区约为 1：3.8，服装、艺术学院女生众多，部分理工科学院男生比例偏高，如机械学院。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li>延安路校区位于上海市长宁区<a href="https://surl.amap.com/XpZezF33wy" target="_blank" rel="noreferrer">延安西路 1882 号</a>，坐落于上海市区，附近有轨道交通 3 / 4 号线。服装与艺术设计学院、工商管理学院、人文学院、上海国际时尚创意学院位于延安路校区（其下开设的专业也当然位于该校区啦）。</li><li>松江校区位于上海市松江区<a href="https://surl.amap.com/XFnvvtj3E9" target="_blank" rel="noreferrer">人民北路 2999 号</a>，东华大学松江校区坐落于松江大学城，汇聚了上海 7 所高校。附近有轨道交通 9 号线（约 2km 远）和松江有轨电车 2 号线，进城需要约 1 个半小时的时间。整条文汇路都是繁华的小吃街，有樱花广场等商圈，校园东北部有松江印象城，是一家较大的商场。材料科学与工程学院、纺织学院、化学与化工学院、环境科学与工程学院、机械工程学院、计算机科学与技术学院、理学院、钱宝钧学院、生物与医学工程学院、外语学院、信息科学与技术学院位于松江校区。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训位于校内，笔者在军训期间并未遇到强制要求剪发的事情。强度适中（上海经常下雨），身体不适可打报告休息。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>体育课选课男女分开，但多数上课时一起上，体育课强度取决于体育老师，除非遇到魔鬼老师，大部分都能过。</p><p>体育课需要打卡，可早起 7：40 去定点地方打卡、下载高校体育软件晨跑（可作弊）、参加活动拿免跑单等方式打卡，40 次满分。</p><p>有保健班课，笔者并未判断 HRT 处方能否申请到该课程。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测为每一个学年一测，有 50m 跑，立定跳远、基础素质、引体向上 / 仰卧起坐、1000m / 800m 跑等内容，不需要游泳等特殊的测试。</p><p>笔者个人认为体测环境较为宽松，很多项目即使不测也能顺利毕业，因此很多人选择不跑 1000m / 800m。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>松江校区的寝室为本科生四人间，硕士生有四人间的老楼和 2 人间的新楼。</p><p>旧楼四人间没有独立卫浴，没有电梯，7 层，统一到 1 层洗澡，洗澡间有和厕所一样的隔间。可以申请换寝室，可以校外租房住，晚上不查房，一般最晚 24 点之前回来宿管阿姨都会给留门。</p><p>新楼二人间有独立卫浴，有电梯，但不是所有人都申请得到。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>松江校区校医院比较鸡肋，更多用于小病开药和报销，2023 年起学校会给学生办医保卡，可以去附近的医院使用无需报销。</p><p>松江校区校外最近的医院是上海市第一人民医院，是三甲医院，可以骑车或坐公交到达，可以进行激素检查，有精神科，笔者遇到的精神科医生友跨。笔者曾经在在上海九院开过激素药之后带着病历本去第一人民医院开到过激素。</p><p>除此之外去一些为跨性别群体提供医疗的大医院大多位于市区，需要耗费一些时间。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>松江校区第三卫生间数量有限。学校内 LGBT 氛围，笔者认为不太强烈。</p><blockquote><p>但笔者遇到的老师和同学都较为友善，向辅导员出柜并未得到歧视和劝阻；笔者曾经到学校的心理咨询中心寻求帮助，得到了心理咨询老师的亲切接待和关怀。</p><p>遇到的人都较为友善，能对性少数群体以平常心对待。</p></blockquote>',27)]))}const g=e(n,[["render",d]]);export{x as __pageData,g as default};
