import{_ as e,c as a,o as l,aB as r}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"大连东软信息学院","description":"","frontmatter":{"title":"大连东软信息学院","author":"若燕","wordCount":927},"headers":[],"relativePath":"campus/DNUI.md","filePath":"campus/DNUI.md","lastUpdated":1685598725000}'),n={name:"campus/DNUI.md"};function i(d,t,o,s,c,h){return l(),a("div",null,t[0]||(t[0]=[r('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">-</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">-</td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.neusoft.edu.cn" target="_blank" rel="noreferrer">大连东软信息学院</a>（Dalian Neusoft University of Information，DNUI）是经国家教育部批准设立，由东软出资举办的一所以工学为主，工学、管理学、艺术学、文学、医学等学科相互支撑、协调发展的民办普通高等院校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校共有软件园校区和博川校区两个校区。所有大一新生均入住博川校区，大二以后除继续教育学院、信息系统与信息管理专业留在博川校区，其他均搬回软件园校区。</p><ul><li>软件园校区位于 <a href="https://amap.com/place/B019B0C3Q7" target="_blank" rel="noreferrer">大连市甘井子区软件园路 8 号</a>，是老校区，周边比较荒凉。</li><li>博川校区位于 <a href="https://amap.com/place/B0HRVATDJX" target="_blank" rel="noreferrer">大连市旅顺口区博川路 8 号</a>，是新校区。步行 15 分钟可到达黄泥川地铁站，去市中心需约 40 分钟，对面是天地软件园。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><ul><li>因为疫情原因，军训由学长学姐以及国旗班来训。 <blockquote><p>我军训时候，有医院开的任何疾病证明都可以坐在旁边看军训，头发取决于辅导员。</p></blockquote></li><li>凭医院开的疾病证明可以免于执行体测。大一都在练习太极扇，无激烈运动。能否申请减免未知。</li></ul><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>生活园区由碧桂园物业负责，包括宿舍门卫，每天都有物业打扫公共区域，博川校区预留有较多特殊情况宿舍，有正当理由跟辅导员申请单间（4 人间类型）学工一般都会批。虽然是单间，查寝、断电、电器限制，以及宿舍评优一个都少不了。学校宿舍均无独卫独浴，澡堂每间有墙隔挡。</p><blockquote><p>如果经济条件允许话，建议申请走读。</p></blockquote><h2 id="医疗资源情况" tabindex="-1">医疗资源情况 <a class="header-anchor" href="#医疗资源情况" aria-label="Permalink to &quot;医疗资源情况&quot;">​</a></h2><ul><li>博川校区内的医疗是东北财经大学医院承包的，只能看一些基础疾病，HRT 药物以及性激素检查什么的，还是去三甲医院好。</li><li>软件园校区内医疗不清楚，但学校对面就是大连医科大学附属第二医院心血管病医院。</li></ul><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><blockquote><p>计软学院辅导员以及学校心理老师很友善（前提是你别玩大的，比如在学校紫砂什么的），尊重你的性别选择，合理的诉求都会满足。学校有几个友跨和跨，<s>带上小雷达去晚自习，大概率会被认出</s>，因为一些原因只跟了辅导员出柜，没有和其他同学出柜，接受程度不太清楚。</p></blockquote><h2 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h2><ul><li>计算机学院：氛围一般，辅导员比较友好，目前发现一个 MtX 以及一个对跨性别有一点了解的同学</li><li>软件学院：授课老师比较苛刻</li><li>智能与电子工程学院：发现一位 MtF</li><li>外国语学院：发现一位 MtX</li></ul>',17)]))}const x=e(n,[["render",i]]);export{p as __pageData,x as default};
