import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"大连工业大学","description":"","frontmatter":{"title":"大连工业大学","wordCount":1167},"headers":[],"relativePath":"campus/DPU.md","filePath":"campus/DPU.md","lastUpdated":1741356272000}'),n={name:"campus/DPU.md"};function d(i,t,h,o,s,c){return r(),a("div",null,t[0]||(t[0]=[l('<p>快跑！！！！！</p><table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">-</td><td style="text-align:center;">仅女生宿舍</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">-</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">可凭疾病诊断见习</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">-</td><td style="text-align:center;">因短期无法治愈的疾病（如先天性心脏病、哮喘、恶性肿瘤等）或身体残疾原因，可持指定医院诊断材料或残疾证办理康复班学习；<br>短期可治愈的疾病（如韧带拉伤、半月板损伤等）以及术后康复（如阑尾炎、骨折等）等情况，不予批准参加康复班学习。</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.dlpu.edu.cn" target="_blank" rel="noreferrer">大连工业大学</a>（Dalian Polytechnic University, DPU），位于辽宁省大连市，大连市境内公办理工类学校，辽宁省 “国内高水平大学重点建设高校”，综合类二本、艺术类一本院校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>大连工业大学位于<a href="https://www.amap.com/place/B019B08H14" target="_blank" rel="noreferrer">大连市甘井子区区轻工苑 1 号</a>，学校出门就是机场和施工工地，噪音污染较重，出门右拐一千米地铁站，学校门口交通不便，经常堵车，从学校正门打滴滴一般需要十分钟才能到学校门口，学校距市区较远，周边生活配套较少。</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训不管头发长度，在篮球场内练军姿正步，军训会持续近一个月。可凭任意疾病诊断证明申请见习。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>体育课上什么需要卡点抢，会网络崩溃抢不到合适的课，不允许跨性别选课，男女课程强度差异较大。学校仅允许持有残疾证明的同学申请见习和通过任意三甲医院开具的 “无法进行体育活动且在大学期间内无法恢复的诊断” 来申请免修，其余情况一概不允。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>尚不明确。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>学校的寝室环境以脏、乱、差著称。</p><h3 id="脏" tabindex="-1">脏 <a class="header-anchor" href="#脏" aria-label="Permalink to &quot;脏&quot;">​</a></h3><p>宿舍楼便内异味较大，公共厕所无人打扫，卫生条件较差。</p><h3 id="乱" tabindex="-1">乱 <a class="header-anchor" href="#乱" aria-label="Permalink to &quot;乱&quot;">​</a></h3><p>宿舍楼内无垃圾桶，楼道内垃圾堆积较乱。</p><h3 id="差" tabindex="-1">差 <a class="header-anchor" href="#差" aria-label="Permalink to &quot;差&quot;">​</a></h3><p>宿舍楼很高，楼梯跨层也很高，上下楼不便。宿舍空间小，没有配套风扇和空调，无独立浴室。独立卫生间仅有女生宿舍（4 人寝）有，男生宿舍（6 人寝）没有，有独立卫生间的宿舍楼过于老旧。无法申请换寝室。回宿舍楼需要爬山。</p><p>学校允许申请走读，需家长陪同（填表和拍视频申请）。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>无校医院，校内药店无 HRT 药物。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p><a href="https://twitter.com/whyyoutouzhele/status/1672239070146117636" target="_blank" rel="noreferrer">2023 年 6 月 23 日校内彩虹旗涂鸦被破坏事件</a>，校内原有的公共涂鸦墙上的彩虹旗被顺直同学用喷漆打叉，并大放厥词，称 “骄傲” 无异于 “随地大小便”。事件发生后在互联网上引起广泛讨论，但校方从未出面。彩虹旗涂鸦被毁后，画彩虹旗的学姐曾两度组织人恢复，但均被顺直同学于凌晨再度破坏，之后画彩虹旗的学姐的小红书账号注销。</p><p>校领导书记歧视性少数群体，认为其是 “违背自然和客观规律的”，看待性少数同学有偏见和歧视。</p><p>学校无性少数相关社团，无性少数相关科普。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>尚不明确。</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>尚不明确。</p><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><p>无论是否为跨性别，来此学校上学就意味着生活水平的严重下降，不建议来。</p><p>校方强制要求使用钉钉进行晚间归寝打卡确认以 “确保学生安全”，若拒绝打卡，需签署 “因不执行打卡出现任何人身安全问题均由学生本人和家长负责，与学校毫无关系” 的承诺书并由家长签字。</p><p><a href="http://jwc.dlpu.edu.cn/engine2/m/0/2215614/3033678?p=439771&amp;t=7250534&amp;currentBranch=1" target="_blank" rel="noreferrer">体育课康复班管理规定</a></p>',36)]))}const u=e(n,[["render",d]]);export{x as __pageData,u as default};
