import{_ as e,c as t,o as r,aB as o}from"./chunks/framework.uEqVN41L.js";const d=JSON.parse('{"title":"福州大学","description":"","frontmatter":{"title":"福州大学","author":["雨音浅夏"],"wordCount":629},"headers":[],"relativePath":"campus/FZU.md","filePath":"campus/FZU.md","lastUpdated":1728116039000}'),l={name:"campus/FZU.md"};function p(i,a,c,h,n,s){return r(),t("div",null,a[0]||(a[0]=[o('<h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.fzu.edu.cn" target="_blank" rel="noreferrer">福州大学</a>（Fuzhou University，FZU）是国家 “双一流” 建设高校、国家 “211 工程” 重点建设大学、福建省人民政府与国家教育部共建高校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校设有旗山校区、铜盘校区、怡山校区等多个校区。</p><ul><li>旗山校区位于 <a href="https://amap.com/place/B024F05T6I" target="_blank" rel="noreferrer">福州市闽侯县学园路 2 号</a>，大部分学生在此就读。</li><li>铜盘校区位于 <a href="https://amap.com/place/B024F061NA" target="_blank" rel="noreferrer">福州市鼓楼区五凤街道铜盘路软件大道 89-1 号</a>，少部分学生在此就读。</li><li>怡山校区位于 <a href="https://amap.com/place/B024F055ER" target="_blank" rel="noreferrer">福州市鼓楼区洪山镇工业路 523 号</a>，福州大学至诚学院设立于此。</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>以下介绍的是旗山校区的情况。</p></div><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><blockquote><p>军训时男女混合进行，服装风格一致，是按专业或班级编排啦，相对较宽松（注意防晒）。</p></blockquote><p>军训对头发长度无要求。</p><p><em>TODO: 待补充军训相关信息，例如是否可以协商免训或半训</em></p><p>大一体育课男女分开教学，强度适中；无校园跑步要求。</p><p><em>TODO: 待补充体育相关信息，例如是否可以协商免修或降低强度等</em></p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><blockquote><p>宿舍楼大部分是按学院而不是性别分配（建筑学院如此，部分学生多的专业好像有分开）（笔者宿舍旁边一间就是学姐 x）… 进宿舍门前不用怕尴尬</p></blockquote><p>宿舍以四人间为主，有独立卫浴，有书桌和衣柜。床铺允许设置帘子（军训也不需要拆除），宿舍检查较宽松。大一可携带电脑，无晚熄灯（只是建筑学院情况）。可以自己购置洗衣机，但阳台晾衣位置有限。可以申请更换宿舍，但一般需要在同一学院（年级不一定）。</p><blockquote><p>麻烦大家来建筑学院报团（大雾）</p></blockquote><p>未发现第三卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p><em>TODO: 待补充医疗相关信息，例如校医院是否可以进行激素检查，是否可以开具 HRT 所需药物；如果在校外医院就诊，是否可以报销</em></p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p><em>TODO</em></p><h3 id="mtf-分布" tabindex="-1">MtF 分布 <a class="header-anchor" href="#mtf-分布" aria-label="Permalink to &quot;MtF 分布&quot;">​</a></h3><blockquote><p>还没有见过其他 MtF 朋友… 因为自己也还是深柜啦。</p></blockquote><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><blockquote><p>学校的店铺很多，饭菜好吃 ww</p></blockquote>',25)]))}const m=e(l,[["render",p]]);export{d as __pageData,m as default};
