import{_ as e,c as a,o as l,aB as r}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"广东药科大学","description":"","frontmatter":{"title":"广东药科大学","wordCount":1101},"headers":[],"relativePath":"campus/GDPU.md","filePath":"campus/GDPU.md","lastUpdated":1739159899000}'),n={name:"campus/GDPU.md"};function d(i,t,s,o,c,h){return l(),a("div",null,t[0]||(t[0]=[r('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:left;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">体育测试凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:left;">见下文</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✗</td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:left;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.gdpu.edu.cn/" target="_blank" rel="noreferrer">广东药科大学</a> （Gongdong Pharmaceutical University, GDPU），是全国三所药科大学之一，是华南地区最早开办药学系列专业的高等学府。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>将按照专业分配校区，请留意志愿填报手册上的专业分布。</p><ul><li><p>广州校区大学城校园位于<a href="https://amap.com/place/B00140VATW" target="_blank" rel="noreferrer">广州市番禺区广州大学城外环东路 280 号</a>，设有药学院、中药学院、生命科学与生物制药学院、基础学院，是主要专业的所在地。</p></li><li><p>广州校区赤岗校园位于<a href="https://amap.com/place/B00140TVAK" target="_blank" rel="noreferrer">广州市海珠区江海大道 283 号</a>，设有公共卫生学院、临床医学院、护理学院和健康学院等。该校园位处市中心，交通较为便利。</p></li><li><p>中山校区位于<a href="https://amap.com/place/B0FFGMDHJ5" target="_blank" rel="noreferrer">中山市五桂山镇长命水大道 9-13 号</a>，校区设有食品科学学院、医药化工学院、医药商学院等。校区较为偏僻，距离中山市中心约 20 分钟车程，门口有直达广州市区的大巴（约 90 分钟）。</p></li><li><p>云浮校区位于<a href="https://amap.com/place/B0FFIHLWLD" target="_blank" rel="noreferrer">云浮市云安区西江新城文华路 368 号</a>，2018 年秋季开始招生办学，现有中医学院、中药资源学院、护理学院、健康学院和医药信息工程学院共 5 个学院。该校区远离云浮城区，非常偏僻，请留意。</p></li></ul><h2 id="军训与体育课" tabindex="-1">军训与体育课 <a class="header-anchor" href="#军训与体育课" aria-label="Permalink to &quot;军训与体育课&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训一般进行 14 天，在校内军训，不要求强制剪发。凭医学证明一般可申请免训，但需到伤病连。作者以失眠为由申请了免训，且未有到伤病连。（获批比较取决于辅导员的态度）</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>大一至大二需每学期需选择一门体育课程，大一学期开始后会通知自行选课，所选课程不分性别（但健美操，瑜伽等课程女生偏多）。大一到大二每学期体育课一部分期末成绩包括在校园跑内，男生 80km，女生 40km，每日最多 5km，可使用自行车等工具完成。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测项目包括：身高、体重、肺活量、50 米、800（女）/1000 米（男）、坐位体前屈、仰卧起坐（女）/ 引体向上（男）、立定跳远。体测每学年一次，可在学校通知网页处下载表格填写后提交体育科申请免测（需要提交疾病诊断证明，性别焦虑等证明可以使用），免测申请一旦通过后下学年便无需再次申请。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>中山校区均为 4 人宿舍，上床下桌，独立卫浴，有空调，无门禁。可以自行购买冰箱（但不要被宿管看到哦）申请调换寝室较难。校区内无第三卫生间。</p><p>大学城学城校区为 4/6 人宿舍，视专业 / 年级变化，独立卫浴，有中央空调（每年开启时间统一）有人脸识别门禁，校区内无第三卫生间。</p><p>云浮校区一期的宿舍为四人间，二期宿舍为六人间。</p><blockquote><p>赤岗校区请参考其他网络信息。</p></blockquote><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医室无法开具激素检查，不能公费购买 HRT 药物，学生医保涵盖范围小且报销条件较为严格，建议自费进行相关检查与药物购买。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>作者所在的中山校区所遇到的同学较为友善，但也有部分私下组织的小群体对 LGBT + 抱有攻击的心态，也请多加留意。</p><h3 id="跨性别分别情况" tabindex="-1">跨性别分别情况 <a class="header-anchor" href="#跨性别分别情况" aria-label="Permalink to &quot;跨性别分别情况&quot;">​</a></h3><p>大学城校区，中山校区均有分布，赤岗与云浮校区未知。</p>',24)]))}const g=e(n,[["render",d]]);export{x as __pageData,g as default};
