import{_ as e,c as r,o as t,aB as l}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"广州大学","description":"","frontmatter":{"title":"广州大学","wordCount":437},"headers":[],"relativePath":"campus/GZHU.md","filePath":"campus/GZHU.md","lastUpdated":1726508156000}'),o={name:"campus/GZHU.md"};function i(n,a,h,s,c,u){return t(),r("div",null,a[0]||(a[0]=[l('<h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="http://www.gzhu.edu.cn/" target="_blank" rel="noreferrer">广州大学</a> （Guangzhou University）</p><h2 id="宿舍情况" tabindex="-1">宿舍情况 <a class="header-anchor" href="#宿舍情况" aria-label="Permalink to &quot;宿舍情况&quot;">​</a></h2><p>本部独卫，6 人寝，无电梯，非上床下桌，公共空间小。空调中午及晚上限时开放。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><ul><li>校医院简陋，仅仅可看感冒等小问题，医保报销麻烦，常常等一个学期还没到账。经过咨询，校医院没有任何 HRT 相关药物。</li><li>本部骑车 10 分钟或公交半小时（含等车时间）可达广东省中医院大学城院区。</li><li>省中医生殖医学科可给有老处方的跨性别人群开具 HRT 药物，通过此法开的药物可走学校医保报销流程，但每天报销上限 70 元。故建议少量多次就诊开药报销。</li><li>诊断证明可通过暨南大学附属第一医院（广州华侨医院）精神心理科贾艳滨处开具。</li></ul><h2 id="地理位置" tabindex="-1">地理位置 <a class="header-anchor" href="#地理位置" aria-label="Permalink to &quot;地理位置&quot;">​</a></h2><p>大学城校区（本部）：位于 <a href="https://amap.com/place/B00140AM0Y" target="_blank" rel="noreferrer">广州市番禺区大学城外环西路 230 号</a>，在商中、北亭旁边，生活、娱乐设施丰富，但离市中心较远，有其他需求需离开大学城较麻烦。</p><p>桂花岗院区（老校区）：位于 <a href="https://amap.com/place/B00140A367" target="_blank" rel="noreferrer">广州市越秀区桂花岗东 1 号</a>。</p><p><a href="https://www.zhihu.com/question/287434367/answer/1217936004" target="_blank" rel="noreferrer">广州大学桂花岗校区？- 知乎</a></p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><ul><li>军训：常规要求。</li><li>体育：常规要求。可抢瑜伽课（不限性别），但课上几乎全是女生。</li></ul><h2 id="校园文化" tabindex="-1">校园文化 <a class="header-anchor" href="#校园文化" aria-label="Permalink to &quot;校园文化&quot;">​</a></h2><blockquote><p>同学间比较包容。</p></blockquote><h2 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h2><ul><li>数学学院：辅导员胡某不支持不反对，持中立态度，不干涉，但也不愿开具介绍信。</li><li>其他学院：待探路。</li></ul>',16)]))}const _=e(o,[["render",i]]);export{p as __pageData,_ as default};
