import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"河南工业大学","description":"","frontmatter":{"title":"河南工业大学","author":["yu_jun_ge"],"wordCount":1523},"headers":[],"relativePath":"campus/HAUT.md","filePath":"campus/HAUT.md","lastUpdated":1728116039000}'),n={name:"campus/HAUT.md"};function d(i,t,p,s,o,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.haut.edu.cn/" target="_blank" rel="noreferrer">河南工业大学</a>（Henan University of Technology，HAUT）</p><p><s>河南空调干饭大学</s></p><p>河南省人民政府、国家粮食局共建高校</p><p>普通本科（有中外合作专业）</p><p>男女比例大概六四开（算是工科类院校的一个例外）</p><p>普通专业学费 5000 / 年 中外合作 18000 / 年 高收费专业 12000 / 年</p><p>2022 年开始嵩山路校区（老校区）停止招收专科 本科文科相关专业大一迁至嵩山路校区教学 大二搬回莲花街校区（莲花街校区为主校区 / 新校区）</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p><a href="https://amap.com/place/B0173082JT" target="_blank" rel="noreferrer">河南工业大学（莲花街校区）</a> 位于河南省郑州市高新区莲花街 100 号。</p><p>距学校最近地铁站为 1 号线终点站河南工业大学站（终点站 人少 平时基本上都有座位） 离西门和南门均大约 500 米。</p><p>学校位于郑州近西四环的位置 去市中心（二七广场）大概 40 分钟 学校附近有两个商圈 周边娱乐还算可以。</p><p>校内有哈罗单车 / 共享助力车。</p><p>南门口有公交站。</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>笔者入学时因民运会占用操场 军训延期至十月下旬。</p><p>军训为学校预备役（学长学姐）带训</p><blockquote><p>强度还算 OK。</p></blockquote><p>不强制剪发（23 级军训时长 21 天）</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>体育课篮球足球排球羽毛球分男女，其余不分性别。</p><p>有校园跑（一天两公里 一学期 60 公里及格 多跑可加期末成绩 最多多跑 20 公里可加 10 分）。</p><p>体育课仅大一大二有。</p><blockquote><p>体育课强度不高（本人乒乓球 比较划水） 期末考核也比较划水。</p></blockquote><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>大学生体育测试： 室内四项（身高 体重 肺活量 坐位体前屈） 室外四项（50 米 引体向上 1000 米 / 800 米 立定跳远）</p><p>室外四项算入体育课期末考核的一部分。</p><p>体育测试计算成绩：前三年体测平均分 + 大四体测得分 / 2，过 50 即算及格。</p><p>校医院不开免体测证明，本人前往省人民医院心理医学科及内分泌科均为成功开具。</p><p>军训和体育相关减免问题本人尚未了解（本人大三才开始的 HRT）</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>2022 年开始嵩山路校区（老校区）停止招收专科，本科文科相关专业大一迁至嵩山路校区教学，大二搬回莲花街校区（莲花街校区为主校区 / 新校区）</p><p>莲花街校区宿舍分为老宿舍和新宿舍。老宿舍为四人间，有独卫无热水有阳台。新宿舍为六人间，有独卫有热水有阳台。</p><p>宿舍楼有院子可晾晒衣物。空调暖气全覆盖，洗浴中心有隔间，无第三卫生间。</p><p>笔者所知申请外宿条件为：家在郑州 / 有心理疾病家长陪读（感觉申请外宿不算太难 本校在外租房的同学不在少数）。</p><p>宿舍有门禁，夜不归宿问题不怎么管（笔者一周两三天不回宿舍）。</p><p>操场 / 标准足球场 / 篮球场 / 乒乓球台 / 网球场均有，800 米标准跑道，东西大操场。</p><p>仨餐厅 + 西门一条小吃街。</p><p>饮品店有上海阿姨 / 益禾堂 / 瑞幸 / 幸运咖 / KFC / 蜜雪冰城 / 京东便利店 get√</p><p>餐厅挺好吃的，大江南北菜系基本上都有。</p><p>学校超市有啤酒没白酒。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>可由校医院开转诊前往外院开药，可走医保。校医院无 HRT 相关药物，但是可转诊。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>笔者中长发、半 pass、已 RLE ，日常女装偏中性出门，在宿舍没碰到啥不方便的，休学前后两任辅导员 + 班主任都是友跨，笔者向家里出柜后师长都帮我和家里沟通过，学校暂未发现很反跨恐跨的，学校对女装这件事好像没什么太大的反应（可能因为都是年轻人吧），我也女装去上过课，也没人说啥。不过女装出门多少有点吸睛（倒也正常 习惯就好）。暂无 LGBT 相关社团，倒是有动漫社，不过动漫社有些人对女装这件事比较反感我是有点想不明白的（讲道理二次元应该对这种相对更包容吧）。目前在学校就挖到一只 MtF（还不敢 HRT），倒是挖到两只 FtM（实属有点罕见了），学校 gay 不少（我认识的就有十几个了），les 不太了解（在表白墙上有见问过的）。学校相对来说对 LGBT 比较友善吧，并不像大众认知里的北方比较 “保”。学校也没有因为我这个情况就劝休学之类的，综合来说算是比较友跨的了。</p><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="yu-jun-ge" tabindex="-1">yu_jun_ge <a class="header-anchor" href="#yu-jun-ge" aria-label="Permalink to &quot;yu_jun_ge&quot;">​</a></h3><ul><li>QQ：104704289</li></ul>',51)]))}const x=e(n,[["render",d]]);export{u as __pageData,x as default};
