import{_ as e,c as a,o as l,aB as r}from"./chunks/framework.uEqVN41L.js";const g=JSON.parse('{"title":"黄淮学院","description":"","frontmatter":{"title":"黄淮学院","author":"Seimo","wordCount":967},"headers":[],"relativePath":"campus/HHU.md","filePath":"campus/HHU.md","lastUpdated":1739159899000}'),n={name:"campus/HHU.md"};function i(d,t,o,s,c,h){return l(),a("div",null,t[0]||(t[0]=[r('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;">仅计算机科学与技术专业</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;">仅计算机科学与技术专业</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">需医院相关证明</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">需医院相关证明</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td><td style="text-align:center;">校医院不作诊疗，校外医院部分可用</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.huanghuai.edu.cn/" target="_blank" rel="noreferrer">黄淮学院</a> Huanghuai University, HHU），黄淮学院是一所公办省属全日制普通本科高校，设有 18 个二级学院，拥有工学、理学、管理学、经济学、文学、艺术学、法学、农学、教育学等 9 大学科门类，在招本科专业 59 个，计算机科学与技术专业本科一批次招生，医学院与国际教育学院为专科招生，其余均为二本招生。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li>校本部位于<a href="https://amap.com/place/B018C0OYJY" target="_blank" rel="noreferrer">驻马店市驿城区开源大道 76 号</a>，为本校主校区，以开源大道为界分为南北两区，北区以理工科类为主，南区以文科，艺术类，体育类为主。两区各有两个食堂，图书馆在南区。</li><li>医学院区位于<a href="https://amap.com/place/B0FFIV3BSJ" target="_blank" rel="noreferrer">驻马店市盘龙山路北段 2628 号</a>，本校医学部所在地，有单独的食堂和小图书馆。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><ol><li>对发型无硬性要求，染发不太张扬即可，时长约为两周，约有一半天数晚自习期间训练，其余晚自习可自行安排。</li><li>可凭相关材料申请免训或缓训，教官为退伍学生，整体可以接受。</li></ol><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><ol><li>大一上统一为太极拳和跳绳，男女不分开，大一下开始根据项目选课，但没有硬性的性别要求，考核为教师打分，基本所有人都会过。</li><li>有校园跑打卡，每学期 50 次，每次男生 2km, 女生 1.6km，计入期末成绩，可以用 FakeLocation，不会有人查后台。</li></ol><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>不计入成绩，按照证件性别区分，男生除立定跳远和引体向上外均为自行报备成绩，女生情况暂不清楚，且所有成绩后期可适当修改。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><ol><li>计算机科学与技术专业使用卓匠书院作为宿舍，六人间有独立卫浴且为上床下桌。其余专业为六人间公共卫浴，但有隔间，北区二号楼为上床下桌，其他非卓匠宿舍均为上下铺，南区全部上床下桌。</li><li>宿舍 24 小时不断电不断网，冬季夜晚会停水，每个宿舍都有空调，wifi，配有无线网口，可以使用路由器，宿舍楼下有充电桩可以给电动车充电，南区有大澡堂。</li><li>校内包括食堂在内所有消费均可扫码支付，支持微信 / 支付宝 / 云闪付 (部分商户未开通)。</li><li>学校门禁为人脸识别，夜晚 11 点后归校会被通报，但是可以在校外通宵等第二天早上再回来。</li></ol><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院不做任何诊疗，需自行到校外医院咨询，具体报销政策取决于各医院。</p><h2 id="贡献者" tabindex="-1">贡献者 <a class="header-anchor" href="#贡献者" aria-label="Permalink to &quot;贡献者&quot;">​</a></h2><h3 id="seimo" tabindex="-1">Seimo <a class="header-anchor" href="#seimo" aria-label="Permalink to &quot;Seimo&quot;">​</a></h3><ul><li>E-mail <a href="mailto:seimo@seimo.cn" target="_blank" rel="noreferrer">seimo@seimo.cn</a></li></ul>',19)]))}const m=e(n,[["render",i]]);export{g as __pageData,m as default};
