import{_ as e,c as a,o as r,aB as n}from"./chunks/framework.uEqVN41L.js";const g=JSON.parse('{"title":"香港科技大学（广州）","description":"","frontmatter":{"title":"香港科技大学（广州）","author":"mini-fish","wordCount":882},"headers":[],"relativePath":"campus/HKUST(GZ).md","filePath":"campus/HKUST(GZ).md","lastUpdated":1739159899000}'),l={name:"campus/HKUST(GZ).md"};function i(d,t,s,o,h,c){return r(),a("div",null,t[0]||(t[0]=[n('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独立卫浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">无障碍宿舍</td><td style="text-align:center;">✓</td><td style="text-align:center;">需医疗证明</td></tr><tr><td style="text-align:center;">无障碍卫生间</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育馆无障碍更衣室</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">游泳馆无障碍更衣室</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.hkust-gz.edu.cn/zh/" target="_blank" rel="noreferrer">香港科技大学（广州）</a>(Hong Kong University of Science and Technology (Guang Zhou) , HKUST (GZ))，位于中国广东省广州市南沙区东涌镇，是香港科技大学、广州大学和广州市人民政府共同举办的一所大学，为粤港澳大湾区规划发布后首个具独立法人资格的内地与香港合作办学机构，也是南沙区内第一所高校。</p><p>香港科技大学（简称港科大）自 1991 年创立以来，秉持锐意创新、与时并进、博大包容、坚持原创的理念， 在教育、科研和知识转移等方面均取得长足进展。短短三十年间，港科大于全球高校排名节节上升，被公认为全球首屈一指的年轻学府。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>香港科技大学目前有香港清水湾校区和广州校区。虽存在法律框架约束，但两校均希望实现 “港科大一体，双校互补” 的理念。</p><ul><li><p>广州校区位于<a href="https://www.amap.com/place/B0IGJURJOJ" target="_blank" rel="noreferrer">广州市南沙区东涌镇笃学路 1 号</a>。</p></li><li><p>香港清水湾校区位于<a href="https://surl.amap.com/1XaJgc163hH" target="_blank" rel="noreferrer">香港西贡区清水湾畔</a>。</p></li></ul><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>简单介绍下广州校区情况～</p><p>学校 2022 年刚成立，校内氛围和清水湾基本一致（<s>University of Stress and Tension</s>），学习课业压力较大。</p><p>宿舍条件优越，本硕博学生目前入住六人套间或八人套间宿舍，每个学生为独立单间，每个套间内共用独立卫浴、厨房、餐厅、起居室。</p><p>存在特殊身心疾病的学生可申请入住单人套间或无障碍套间，已婚学生可申请入住夫妻套间。</p><p>目前校内宿舍楼栋分为本科生宿舍楼、单性别研究生宿舍楼、已婚学生宿舍 / 教职工宿舍楼。</p><p>性身份障碍患者可在入学前与 Student Quarter Working Team (GZ) 联系，提供相关医疗证明申请单人套间（位于已婚学生 / 教职工宿舍楼）。</p><p>根据《精神卫生法》第四条、第七十七条和《个人信息保护法》第二十八条，性身份障碍患者有权不公开性身份（根据自身 RLE 状况和社交关系自主选择是否透露个人性身份）。</p><p>此外，校内无障碍卫生间配套齐全（均设立单性别卫生间和无障碍卫生间），并有明确标识，校内体育馆及游泳馆亦设立无障碍更衣室。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>学校目前有校医室，具体药物情况待探明。</p><h2 id="少数群体友善梗概" tabindex="-1">少数群体友善梗概 <a class="header-anchor" href="#少数群体友善梗概" aria-label="Permalink to &quot;少数群体友善梗概&quot;">​</a></h2><p>校内成立多元与平等机会专委会，参照中国内地法律处理有关骚扰、歧视、诽谤、中伤等行为的投诉，平时亦关照不同师生群体的学习工作生活。</p><h2 id="联系人信息" tabindex="-1">联系人信息 <a class="header-anchor" href="#联系人信息" aria-label="Permalink to &quot;联系人信息&quot;">​</a></h2><h3 id="mini-fish" tabindex="-1">mini-fish <a class="header-anchor" href="#mini-fish" aria-label="Permalink to &quot;mini-fish&quot;">​</a></h3><ul><li>E-Mail: <a href="mailto:Mini-Fishhh@proton.me" target="_blank" rel="noreferrer">Mini-Fishhh@proton.me</a></li></ul>',23)]))}const u=e(l,[["render",i]]);export{g as __pageData,u as default};
