import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"黑龙江职业学院","description":"","frontmatter":{"title":"黑龙江职业学院","author":"浅羽织雪","wordCount":612},"headers":[],"relativePath":"campus/HLJP.md","filePath":"campus/HLJP.md","lastUpdated":1739159899000}'),n={name:"campus/HLJP.md"};function d(i,t,s,h,o,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;">没病历不可减免</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">凭病历免训</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">-</td><td style="text-align:center;">取决于辅导员</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">-</td><td style="text-align:center;">你跟老师说肚子疼就可以在旁边看着</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.hljp.edu.cn/zsw/bzsy.htm" target="_blank" rel="noreferrer">黑龙江职业学院</a>（Heilongjiang Polytechnic, HLJP）， 黑龙江职业学院（黑龙江省经济管理干部学院）是一所教育部批准成立的公办全日制普通高等职业学校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>南岗校区位于<a href="https://surl.amap.com/20KBEwkm2nF" target="_blank" rel="noreferrer">哈尔滨市 南岗区 学府路</a>，一般来说为 理工学生日常学习的校区，附近有师大夜市小吃街、学府凯德等商场。</p></li><li><p>双城校区位于<a href="https://surl.amap.com/FVGBWVF4CF" target="_blank" rel="noreferrer">哈尔滨市 双城区 迎宾路</a>，一般来说为 文综学生日常学习的校区。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>做个病历可以免训，在旁边坐着看，不强制要求剪头发但是可能教官会有意见（？） 军训 7-15 天 （我那届只训 7 天）。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>老师好说话，有的时候可以翘课。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>本人体测当天和导员说身体不舒服没测，后续没补测，混过去了。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>一校区，八人寝，上床下床，桌子只有四张，大一早上 6 点要跑操，没收拾好卫生不许去上课，无独立卫浴，无第三卫，可以租房走读。</p><p>二校区，不清楚。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>无，医保可报销部分校外医院费用。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>个别专业导员友跨且友善，尊重理解但不提倡。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>跨性别分布较少，一届 1-2 个。</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>建筑工程专业导员较友善，会了解情况。</p><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="浅羽织雪" tabindex="-1">浅羽织雪 <a class="header-anchor" href="#浅羽织雪" aria-label="Permalink to &quot;浅羽织雪&quot;">​</a></h3><ul><li>twitter：@Oriyukiiiii</li></ul>',26)]))}const u=e(n,[["render",d]]);export{p as __pageData,u as default};
