import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"黑龙江大学","description":"","frontmatter":{"title":"黑龙江大学","author":["AyasakiAyana","billchenchina"],"wordCount":1865},"headers":[],"relativePath":"campus/HLJU.md","filePath":"campus/HLJU.md","lastUpdated":1729083727000}'),n={name:"campus/HLJU.md"};function i(d,t,o,p,s,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">仅研究生宿舍</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓（可以，但没必要，因为很水）</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">见下文</td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">见下文</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">只能处理日常疾病🤔</td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.hlju.edu.cn" target="_blank" rel="noreferrer">黑龙江大学</a>（Heilongjiang University，HLJU），属于黑龙江省与国家国防科技工业局共建高校，学校学科种类设置齐全，设有教学院部 30 个，开设本科专业 86 个；有博士后科研流动站 9 个，一级学科博士点 11 个，一级学科硕士点 35 个，硕士专业学位类别 25 个。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li>本部位于哈尔滨市南岗区 <a href="https://amap.com/place/B01C302AE4" target="_blank" rel="noreferrer">学府路 74 号</a>。</li><li>呼兰区有一分校区，据校园信息网报道，最近修缮中，预计数年内启用供部分本科生（仅部分学院）及部分农学类研究生使用。</li></ul><p>主校区位于学府商圈，与哈西商圈、秋林商圈相接壤，哈尔滨学院、哈尔滨理工大学、哈尔滨师范大学、东北林业大学以及哈尔滨医科大学均在黑龙江大学周围。学校学府路 74 号正门出门即是 <a href="https://amap.com/place/BV10114433" target="_blank" rel="noreferrer">哈尔滨地铁一号线黑龙江大学站</a>，自 C 区学府东四道街校门出校后步行约 1 公里可到达 <a href="https://amap.com/place/BS11714428" target="_blank" rel="noreferrer">哈尔滨地铁三号线征仪路站</a>，周围公交站及线路众多。同时学校临近哈尔滨西站，可乘地铁前往哈尔滨站、哈尔滨东站以及哈尔滨北站。机场可乘学校门口的机场巴士。 \\</p><p>主校区共设有 ABCD 四个区，其中 AB 二区连通，经由 B 区测绘路地下通道可到达 C 区，从 B 区学校汇文楼大门出门后步行约一百米可到达 D 区。</p><p>呼兰校区位于哈尔滨市 <a href="https://amap.com/place/B01C30322A" target="_blank" rel="noreferrer">呼兰区东府路 752 号</a>，位于呼兰火车站旁，位置远离哈尔滨市区，周边以村镇为主，无任何其他资源。预计校区启用后可能会开行与主校区之间班车（尚不确定），旁边呼兰火车站有到达哈尔滨东站的车次，通过哈尔滨东站可到达哈尔滨道外区，周围三甲医院仅哈尔滨市第二医院，周围其他医院医疗能力存疑，不建议前往。</p><blockquote><p>学校内部面积很大，前期不依靠导航很容易迷路。解封可以在周围夜市逛逛，很热闹。</p></blockquote><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><p>学校军训均由校退役大学生士兵协会承担训练任务，极少部分学院有当地部队承担训练任务，军训虽可协商或凭医疗证明减免，但按照 2023 年重启军训后的效果看通常训练强度极轻（不包括部队承训的学院），主要训练强度集中在内务整理上，因此个人觉得没必要费心思走手续办理减免。</p><p>体育在第一学期为学校统一选课并直接生成在课表，任课老师普遍对学生要求较低，2-4 学期为自主选课，由于学校为综合类大学，学校规模极大，因此选课资源也较多，此处推荐选择球类课程，普遍强度较低，避雷定向越野（强度高）和游泳（涉及更衣问题）类课程。特殊情况可自行向体育教学教研部联系咨询解决方案。四个学期的体育课程为强制修读，不可减免，否则影响毕业！</p><p>体测同样的水，无论成绩什么样也不会耽误什么事，实在懒的去可以去校园集市找人代考（但有被抓风险，自行承担！），可以凭医疗材料办理减免，作者意见依然是可以但没必要</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>宿舍为学校统一按照学院集中分配，无法自主选择！</p><p>研究生统一在 C 区研究生公寓大楼住宿，有独立洗手间但无独立洗浴间，以二人和四人寝为主</p><p>本科生分布在 ABCD 四个区，其中 A 区普遍为六人和八人间，年代久远。B 区有部分大多数四人寝及八人寝，C 区全部为四人寝，极少量二人寝室位于部分宿舍楼楼顶。全部无独立卫浴。</p><p>D 区有新修建宿舍的计划，但还遥遥无期。</p><p>学校内不存在第三卫生间！C 区有一原留学生公寓，现作为酒店可在携程预订，提供标准酒店式服务，但体验一般。</p><blockquote><p>AB 区有澡堂，但均为北方大澡堂，你懂的</p></blockquote><p>C 区澡堂设置有隔断和帘子，可部分保护隐私</p><p>学校整体饮食质量较高，但价格也较高。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>学校有校医院，位于 A 区，仅处理常见疾病，备有常用药物，价格低廉，学生就诊直接以医保价格结算，但医疗能力有限。</p><p>A 区主门对面的解放军 962 医院和 C 区出门后约一公里的哈尔滨医科大学附属第二医院均为哈尔滨顶级医疗资源，可满足绝大多数医疗需求，可开激素六项检查和凭处方购买激素药物，哈尔滨医科大学附属第一、第四医院有跨性别精神心理科医疗资源，但报告较少。</p><p>C 区旁哈尔滨医科大学附属第三医院为肿瘤医院，无前往的必要。</p><p>学校大一会统一协助办理三代社保卡，持三代社保卡前往哈尔滨市辖区内医院可直接医保结算，无需另外走报销手续。学校提供的商业保险会在每月有固定 2-3 天在学校办公，可在此期间办理报销事宜。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><blockquote><p>学院辅导员普遍友善，部分年龄较大辅导员除外。本科生按学校规定普遍不允许走读，但辅导员一般会根据实际情况处理，例如办理走读手续，或办理走读手续并且保留宿舍床位等多种处理方式。</p><p>对 T 选择性友好，友好程度取决于 pass 程度</p><p>其他关于 LGBT 类内容普遍友好，但勿触及政治正确类话题，有因类似行为被攻击的先例。</p><p>心理咨询类相关资源建议直接前往哈尔滨医科大学附属第一医院临床心理科，题主有陪诊经历，虽无法开具医疗证明但医生对跨性别内容有了解，且态度普遍友好。</p></blockquote><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="ayasakiayana" tabindex="-1">AyasakiAyana <a class="header-anchor" href="#ayasakiayana" aria-label="Permalink to &quot;AyasakiAyana&quot;">​</a></h3><ul><li>Email: <a href="mailto:misakaliu_6072@foxmail.com" target="_blank" rel="noreferrer">misakaliu_6072@foxmail.com</a></li></ul>',32)]))}const b=e(n,[["render",i]]);export{u as __pageData,b as default};
