import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"济宁医学院（太白湖校区）","description":"","frontmatter":{"title":"济宁医学院（太白湖校区）","author":"蝶依（超级无敌小蝴蝶版）","wordCount":1680},"headers":[],"relativePath":"campus/JNMU - Taibaihu.md","filePath":"campus/JNMU - Taibaihu.md","lastUpdated":1746759978000}'),n={name:"campus/JNMU - Taibaihu.md"};function i(d,t,h,o,s,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;">无独浴。公共卫浴每楼层一个（部分宿舍楼是两层一个），大部分时间不用排队，有独立隔间和帘子</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">二级以上医疗机构书面证明</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">二级以上医疗机构书面证明</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;">校内卫生室药很少，但距济医附院（三甲）仅 300m</td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.jnmc.edu.cn/" target="_blank" rel="noreferrer">济宁医学院</a>（Jining Medical University, JNMC），济宁医学院是由山东省人民政府举办的高等医学院校，入选国家首批卓越医生教育培养计划、山东省卓越工程师教育培养计划，是精神医学专业本科国内办学最早、招生规模最大的四所高等院校之一。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>太白湖校区 位于<a href="https://ditu.amap.com/place/B02190AXWJ" target="_blank" rel="noreferrer">济宁市 任城区 荷花路 133 号</a>，是主校区，一般来说为 临床、护理、法检、公卫、康复 等专业学生 大一 到 大三 日常学习的校区，附近有 太白湖景区、济医附院太白湖院区、济宁吾悦广场等。笔者现就读于此校区，本文档内容仅针对此校区。</p></li><li><p>任城校区 位于<a href="https://ditu.amap.com/place/B0219057F4" target="_blank" rel="noreferrer">济宁市 任城区 建设南路 45 号</a>，是最老的校区，基础设施老旧，一般来说为部分专业大四大五学生学习、见习的校区，附近有 济医附院本部、济宁汽车总站、济宁火车站（仅普速），济宁万达广场。</p></li><li><p>日照校区 位于<a href="https://ditu.amap.com/place/B027902TGV" target="_blank" rel="noreferrer">日照市 东港区 学苑路 669 号</a>，一般来说为 药学相关专业 和 部分非医药专业 学生日常学习的校区，部分专业学生大四大五会来这里见习。</p></li><li><p>高新区科教园 位于<a href="https://ditu.amap.com/place/B0FFG6XUKZ" target="_blank" rel="noreferrer">济宁市 高新区 海川路 16 号</a>，专业数量最少，一般来说为 食品工程 学生日常学习的校区。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><p>笔者现就读于济宁医学院太白湖校区，以下内容仅针对此校区。</p><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>济医军训相对宽松，教官少数为外来人员，大部分为国旗护卫队的学长学姐，基本不会苛责同学，且时间安排合理，中午下午高温时段停训，早晚训练。军训时长各届略有出入，据笔者观察，24 级新生军训时长约 14 天。据笔者同窗介绍，军训可凭二级医疗机构出具的证明减免，军训期间补修康复课程即可获得同等学分。其他身体不适亦可向教官或辅导员请假，但能否全程减免军训尚未明确。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>济医体育课相对宽松，可选课程如足球、篮球、武术等，不按性别区分。据笔者所遇及所闻，体育老师大多和蔼可亲，课堂训练内容相对轻松，考核难度较小。据笔者班级体委介绍，体育课可凭二级医疗机构出具的证明减免，补修康复课程即可获得同等学分。协商减免体育课尚无相关案例。</p><h3 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h3><p>太白湖校区体测难度适中。所有项目均为仪器测定，其中长跑（800/1000）项目管理严格，其他项目如坐位体前屈、跳远、肺活量等相对宽松，有替考机会，但需要注意的是，替考被查处可能会通报，当年无法评奖评优和入团入党。据笔者班级体委介绍，体测可凭二级医疗机构出具的证明减免，是否能够获得同等学分尚未明确。协商减免体测尚无相关案例，且合规性存在疑问。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>笔者现就读于济宁医学院太白湖校区，以下内容仅针对此校区。</p><p>各校区寝室环境参见济宁医学院微信公众号。太白湖校区是 4 或 6 人间。宿舍有独卫，每两层至少 1 个公共浴室，有帘子和隔档。每个浴室旁边是公共洗衣房，洗衣机扫码使用。宿舍限电 800w，超载断电，30 分钟后自动恢复。学校范围内有免费 WiFi，限速 2.5MB/s。宿舍可以挂床帘。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>笔者现就读于济宁医学院太白湖校区，以下内容仅针对此校区。</p><p>学校距离济宁医学院附属医院（太白湖）院区（三甲）300m，从东门出发，步行 10 分钟即可到达，可开具激素六项检查。距济宁精神卫生中心戴庄医院 14km，暂无成功开具易性症证明的报告。截至 2025 年 4 月，济宁市内药店（如广联医药）可无门槛买到补佳乐。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>由于济宁地处孔孟之乡，校内风气保守，笔者推断校内友跨人数可能较少。但济医日常管理较为宽松，学业考核难度也较其他医学院校更低，达到本科毕业要求较为轻松。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>笔者现就读于济宁医学院太白湖校区，以下内容仅针对此校区。</p><p>极少，目前已知只有个位数，且笔者未能与其取得联系。</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>笔者的专业是 临床医学（省属公费医学生） ，本专业属于特殊历史时期的产物，毕业即定向分配，在当地基层医疗机构工作至少 6 年。由于没有找工作的压力，学业压力相较普通临床专业轻松很多。其他专业暂不了解。</p><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><p>暂无</p><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="蝶依-超级无敌小蝴蝶版" tabindex="-1">蝶依（超级无敌小蝴蝶版） <a class="header-anchor" href="#蝶依-超级无敌小蝴蝶版" aria-label="Permalink to &quot;蝶依（超级无敌小蝴蝶版）&quot;">​</a></h3><ul><li>telegram：@luotianyi_butterfly</li><li>X：@0712_luotianyi</li></ul>',32)]))}const x=e(n,[["render",i]]);export{u as __pageData,x as default};
