import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const g=JSON.parse('{"title":"江南大学","description":"","frontmatter":{"title":"江南大学","wordCount":976},"headers":[],"relativePath":"campus/JiangNan.md","filePath":"campus/JiangNan.md","lastUpdated":1726508156000}'),n={name:"campus/JiangNan.md"};function d(i,t,s,c,o,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;">大部分宿舍有独卫（北区一些旧公寓没有），少部分宿舍有独立浴室，公共浴室状况未知</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✓</td><td style="text-align:center;">个别学院楼有，二教无，其余未知</td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.jiangnan.edu.cn/" target="_blank" rel="noreferrer">江南大学</a>（Jiangnan University）。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>几乎全部学生位于<a href="https://amap.com/place/B01FE0I99I" target="_blank" rel="noreferrer">蠡湖校区</a>（无锡市滨湖区蠡湖大道 1800 号，位置较偏远）。</p><p>亦有位于宜兴的<a href="https://amap.com/place/B0HUDUK2ZY" target="_blank" rel="noreferrer">东氿校区</a>（无锡市宜兴市太湖大道与范蠡大道辅路交叉口西南 300 米）（似乎是分开招生的？）</p><p>尽管学校主校区分为南北两区，实际上是一个整体，之所以分为南北两区是因为便于行政管理和教学生活设施的分配。 主校区位于无锡近郊，从机场打车过去大约需要 50 元以上，学校东门口亦有地铁站，虽略偏远但交通便利。 从北门出门过马路就是机厅 学校比较大，目测占地面积估计约 3.5 平方千米，快递点也零散分散在校园各处，如果不会骑车可以选择坐校园小公交（票价 2 元，校园卡 8 折） 无锡精卫中心离的有点远，非高峰期乘出租车约 25 分钟路程</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><blockquote><p>涉及军训似乎可通过辅导员进行减免，具体细节未知</p></blockquote><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>体育课可凭借校医院开具的证明在体育中心申请保健班，校医院证明的办理如果持有三甲医院的含有「不宜剧烈运动」相关字样可以立刻在校医院处通过并抄一张校医院的诊断证明给你。是否适用于易性症暂不明确（按照过往经验还是尽量少对学校领导层出柜），校医院较为偏向本地医院，而无锡精卫对 LGBT 的友善程度似乎不算太高，笔者这里用的是抑郁症的诊断证明。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>指派男性请慎重考虑江大理工科相关专业，此类专业大多数位于较旧的北区（个别宿舍楼还有臭名昭著的七人寝），选择专业时请务必注意。南区宿舍楼基本可保证独立卫生间和四人寝上床下桌。</p><p>由于宿舍床位紧张，办理走读会要求退宿，附近房价不是很便宜，经济困难建议合租。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>可用医保。校医院可用程度未知。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>辅导员大多数是年轻人，包容能力较强。</p><blockquote><p>目前遇到的学院相关工作人员未见有明显恐性少数</p></blockquote><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><ul><li><s>从北门出门过马路就是机厅</s></li><li>学校比较大，目测占地面积估计约 3.5 平方千米，快递点也零散分散在校园各处，如果不会骑车可以选择坐校园小公交（票价 2 元，校园卡 8 折）</li><li>无锡精卫中心离的有点远，非高峰期乘出租车约 25 分钟路程</li></ul>',22)]))}const p=e(n,[["render",d]]);export{g as __pageData,p as default};
