import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const g=JSON.parse('{"title":"南通大学","description":"","frontmatter":{"title":"南通大学","author":["綾瀬桃桃"],"wordCount":1042},"headers":[],"relativePath":"campus/NTU.md","filePath":"campus/NTU.md","lastUpdated":1741878131000}'),n={name:"campus/NTU.md"};function d(i,t,o,s,c,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.ntu.edu.cn/" target="_blank" rel="noreferrer">南通大学</a>（Nantong University, NTU）<s>不是南洋理工大学或者国立台湾大学</s></p><blockquote><p>南通大学是真的 <s>只适合男同</s> ，不适合跨（原因见下）</p></blockquote><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>啬园校区位于<a href="https://amap.com/place/B0FFFEEHJJ" target="_blank" rel="noreferrer">南通市崇川区啬园路 8 号</a>，除医学院、护理学院等少数学院外基本均在该校区，靠近地铁一号线南通大学站、盘香路站。西门出去骑车 5 分钟即可到达中央商务区 <s>（此时打 mai 的要开始激动了）</s> ，北门靠近青创园。</p></li><li><p>启秀校区位于<a href="https://amap.com/place/B0201086QB" target="_blank" rel="noreferrer">南通市崇川区养老院路 18 号</a>，医学院、护理学院在此校区，靠近地铁二号线易家桥站。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训时间在大一入学后约 3 天之后，时长在半个月之内。对头发无要求（长发 + 染发均不受影响）。对于是否可免训（见习）未知，大概率不行。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>大一大二开设体育课，每学年初会进行选课，可选择较轻松项目完成体育课考核（不单独开设保健班）。且大一大二会存在校园跑（步道乐跑），如难以完成可找朋友代 <s>骑</s> 跑或付费代刷。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p><strong>已知单凭 “易性症” 诊断 HRT 病历无法免除体测（友情提示：别找辅导员瞎问有关持相关病历是否可免除体测的问题，小心被迫出柜）。</strong> 如有医生开具的不宜免测证明是否可免除未知。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>啬园校区男生宿舍为 4~6 人间（入学时根据学号进行分配，不可选择），不是独浴（且新宿舍楼情况还好些，是带有隔间的大浴室，老宿舍楼没有隔间），因此对于正在进行 HRT 的同类 <strong>非常不友好</strong> ，<em><s>建议校外租房（非南通市区学生走读需家长知情同意，因此可以尝试以其他理由请求外宿）</s></em> <s>于是出去住了之后发现出勤比上学路程短多了</s> 。</p><p><strong>Upd 2025-3-12: 由于事逼学校甚至还派人上门查走读学生的家长陪同情况？？？所以建议 有 多 远 绕 多 远 走 ，这学校不能待</strong></p><p>校内各处均无第三卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院情况未知。已知啬园校区附近的南通市第一人民医院可开具激素检查，药物是否可购买未知。位于启秀校区也可前往南通大学附属医院进行检查。目前新生疑似无法通过校医院转诊方式获得校内二次报销。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>有关学校态度未知。根据我目前观察来看，老师不知情也不会有太多奇怪的看法（只是有些老师会对 “穿裙子扎辫子的男生” 有些好奇）。我身边一部分同学（以女生居多）能很好的接纳我 <s>（五个女生带一个我都宣称全女组了）</s> 。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>除了我之外没有见过其他同类出现，大概是没有。</p><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="綾瀬桃桃" tabindex="-1">綾瀬桃桃 <a class="header-anchor" href="#綾瀬桃桃" aria-label="Permalink to &quot;綾瀬桃桃&quot;">​</a></h3><ul><li>E-Mail：<a href="mailto:me@momokko.moe" target="_blank" rel="noreferrer">me@momokko.moe</a></li><li>Twitter: <a href="https://twitter.com/Momoko_Ayase" target="_blank" rel="noreferrer">@Momoko_Ayase</a></li><li>Telegram: <a href="https://t.me/launchcore" target="_blank" rel="noreferrer">@Launchcore</a></li></ul>',27)]))}const x=e(n,[["render",d]]);export{g as __pageData,x as default};
