import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"西北大学","description":"","frontmatter":{"title":"西北大学","author":["何度"],"wordCount":503},"headers":[],"relativePath":"campus/NWU.md","filePath":"campus/NWU.md","lastUpdated":1726637627000}'),n={name:"campus/NWU.md"};function d(i,t,o,h,s,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✗</td><td style="text-align:center;">硕博楼除外</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✔️</td><td style="text-align:center;">随便请假，会扣分</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✔️</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✔️</td><td style="text-align:center;">甚至还有长发的选成优秀营员的</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✔️</td><td style="text-align:center;">三甲医院证明进保健班</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="http://www.nwu.edu.cn" target="_blank" rel="noreferrer">西北大学</a>（Northwest University, NWU），位于陕西省西安市，211 工程学校，陕西省属大学。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p><a href="https://amap.com/place/B001D08OO7" target="_blank" rel="noreferrer">长安校区</a>位于西安市长安区学府大道 1 号，大部分学生都在这个校区。</p><p><a href="https://amap.com/place/B001D08M0F" target="_blank" rel="noreferrer">太白校区</a>位于西安市太白北路 1 号。少部分学生例如生科地质食品医学在这个校区。</p><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>不强制剪头发，不要求卸饰品，不查寝。可以协商请假，无需医院证明。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>保健班需要三甲医院证明，平时可以请病假，一次扣 2 分。</p><h3 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h3><p>男生跑 1000，引体向上。50 分合格。保健班可以不测。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>没有第三卫生间。六人寝上床下桌，没有独立卫浴，需要去澡堂洗澡。澡堂和厕所有隔间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>不清楚，目前笔者是自费的。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><blockquote><p>号称西北女同大学，很多女同性恋。至于 G/B/T 不是很确定。</p></blockquote><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>不清楚</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>信院感觉是有的。</p><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="何度" tabindex="-1">何度 <a class="header-anchor" href="#何度" aria-label="Permalink to &quot;何度&quot;">​</a></h3><ul><li><a href="mailto:hedu2023@outlook.com" target="_blank" rel="noreferrer">EMail</a></li></ul>',26)]))}const u=e(n,[["render",d]]);export{p as __pageData,u as default};
