import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"西南大学","description":"","frontmatter":{"title":"西南大学","author":["蘑菇","花盆"],"wordCount":915},"headers":[],"relativePath":"campus/SWU.md","filePath":"campus/SWU.md","lastUpdated":1734361766000}'),n={name:"campus/SWU.md"};function d(i,t,s,c,o,p){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">有正当理由可以直接免除</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p>西南大学（英语：Southwest University，缩写作：SWU），简称 “西大”，位于重庆市北碚区缙云山山麓，是 2005 年 7 月经中国教育部批准，由原西南师范大学、原西南农业大学合并组建的教育部直属重点综合性大学，是中国首批双一流高校，211 工程建设学校，985 优势学科创新平台项目建设大学，是国家布局在中西部的高水平综合型大学。 综合大学中唯一的师范，师范大学中唯一的综合。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>北碚校区位于<a href="https://surl.amap.com/1AuT4hB4bZ8" target="_blank" rel="noreferrer">重庆市北碚区天生路 2 号</a>，一般来说为大多数专业学生学习生活的地方，距离重庆中心城区较远，前往重庆市中心通常需乘坐一个小时左右的地铁。附近没有大型商场，只有 2 号门附近的天生丽街。</p></li><li><p>荣昌校区位于<a href="https://surl.amap.com/298R8Ieo3jX" target="_blank" rel="noreferrer">重庆市荣昌区学院路 160 号</a>，荣昌校区主要是动物医学院，动科学院，水产学院和商贸学院，去往荣昌校区可从北碚校区乘校车前往或乘坐高铁到荣昌站后打车前往。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><p>军训不强制要求剪发，时间长度不定，大约是 2 周，不排除因为各种原因推迟或加长军训时间。</p><p>军训如有正当理由可以直接申请免训，但主要看辅导员的态度。</p><p>体育课从大一开始就是男女分开上课，内容不定。期末要求男 2.4km 女 2km 长跑测试。（会有心善的老师放水捞人少记几秒的长跑之类）</p><p>可凭借相关医学证明申请保健课，会轻松很多</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>校内宿舍 4 到 6 人间不等，有独立卫浴（在重庆上学做好上下爬 n 层楼的准备）。</p><p>不同园区宿舍内基础设施差异较大，但总的来说都不限制安装床帘，摆放个人物品以及不限电。</p><p>可以以易性证或抑郁焦虑等理由向学院申请外宿，校内校外都有租房。</p><p>未在校内发现有第三卫生间或无障碍卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>恒温游泳馆附近的校医院可以检查激素六项，并且不会过多询问。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>部分学院会要求班委统计上报性少数群体名单（已知化工学院，美院）</p></div><p>也有学院氛围友善，辅导员和院领导能理解并且给予部分支持</p><p>心理学部还有不少针对跨性别的研究报告</p><p>据校内术后娘所述教务处对术后修改学籍效率非常高</p><p>校内有不少性少数抱团</p>',23)]))}const g=e(n,[["render",d]]);export{x as __pageData,g as default};
