import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"上海科技大学","description":"","frontmatter":{"title":"上海科技大学","wordCount":622},"headers":[],"relativePath":"campus/ShanghaiTech.md","filePath":"campus/ShanghaiTech.md","lastUpdated":1726508156000}'),n={name:"campus/ShanghaiTech.md"};function i(o,t,c,h,d,s){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.shanghaitech.edu.cn" target="_blank" rel="noreferrer">上海科技大学</a>（ShanghaiTech University，ShanghaiTech）是一所由上海市人民政府与中国科学院共同举办、共同建设，由上海市人民政府主管的全日制普通高等学校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>浦东校区（主校区）位于 <a href="https://amap.com/place/B0FFF3YA07" target="_blank" rel="noreferrer">上海市浦东新区华夏中路 393 号</a>，张江高科技园区附近。门口有地铁 13 号线通过，可比较简单地前往市中心（耗时较长），距离迪士尼较近。</p></li><li><p>岳阳路校区位于 <a href="https://amap.com/place/B00157I2GO" target="_blank" rel="noreferrer">上海市徐汇区岳阳路 319 号 8 号楼</a>。现已非教学校区。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><p>军训位于东方绿洲，项目较轻松，无强迫剪发情况。宿舍为 16-22 人一间，公共淋浴间。</p><p>体育课项目较多，可自选，测试通过难度不高。有健美操等课程，男生可选。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>本科宿舍三人间上床下桌，有中央空调和独立浴室，公共厕所有独立隔间，每层有吹风机。本科宿舍男女同楼，宿管友好。</p><p>研究生宿舍二人间宾馆标准间配置，独立卫浴，每层有吹风机。</p><p>万一和室友不合，调整宿舍也很方便。</p><p>教学楼内每层有厕所，且分蹲便与坐便，有隔间，无第三卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院资源很少，只能治疗一些小病。激素检查等建议去 1km 外的 <a href="https://amap.com/place/B00151E68A" target="_blank" rel="noreferrer">曙光医院</a>。</p><blockquote><p>向值班医生询问（能否进行激素检查及开药）后，告诉我的结果是不行。</p></blockquote><p>心理咨询中心的老师人都很不错（就是比较难排号）</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">本段写于 2021 年。</p></div><blockquote><p>同学较为包容，目前已知学校有留长发的男生，有穿 Lo 裙上课的男生，有公开宣布出柜的男生。无辅导员（辅导员是什么，能吃吗），替代为每级一人的书院制。经验而言，可能会采取无视态度。动漫社团有较多人在 Comic Up 出过 Cos，但 MtF 目前暂未发现（潜伏中）。</p></blockquote>',20)]))}const b=e(n,[["render",i]]);export{u as __pageData,b as default};
