import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"文华学院","description":"","frontmatter":{"title":"文华学院","author":["暮雨"],"wordCount":773},"headers":[],"relativePath":"campus/WHC.md","filePath":"campus/WHC.md","lastUpdated":1732340909000}'),n={name:"campus/WHC.md"};function d(i,t,o,s,c,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://example.rle.wiki/" target="_blank" rel="noreferrer">文华学院</a>（WHC），文华学院是一所民办普通本科学校。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li>光谷校区位于<a href="https://amap.com/place/B001B162BX" target="_blank" rel="noreferrer">武汉市洪山区东湖新技术开发区文华园路 8 号</a>，所有学生在光谷校区上课。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训 14 天 不管头发，凭生理或精神证明可以免除军训 (免训需要写 10 篇 1500 字的读后感)。</p><p>军训不是很严，可以因病去校医院室休息，按照规定请假必须要导员同意，并附上 1500 字读后感，不过可以直接向教官请假，教官同意休息后直接溜回宿舍？</p><blockquote><p>ps: 本人找李赋开了一个不适宜剧烈运动超级好！不过每个学期还有要求 30 次的乐跑，乐骑 (笑) 每次要求 1.6km。</p></blockquote><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>体育课男女一起上，强度看自己选的项目。本人选的体育舞蹈，运动量一般。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测凭医生开具证明可以免除。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>都是四人间，上床下桌，独立卫浴，没有洗衣机，要去楼层公共洗衣间，洗一次衣服 4-5 块钱，衣服都是晾在屋内，有点尴尬，吹风机 0.1 一分钟，洗澡热水一分钟一块钱，晚上 12 点断热水，夏令时不熄灯，冬令时每天晚上 23 至隔天 6 点断电，宿舍限电 1000w。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>不能开局激素证明，校医院就是很小的一个诊所。只能看基础的感冒发烧之类的不要期待有 hrt 药物，校内医院医保报销比例较高，校外医保保险有限。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>学校心理咨询中心的办公桌上插着一面 LGBT 旗帜，心理咨询师较友善，不过咨询时没怎么谈性别焦虑的问题。</p><blockquote><p>据传：xyn 学姐不少（</p></blockquote><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>导员中立偏友跨？笔者是信息学部学生，最开始给导员提交的证明上写明了性别障碍，在导员的帮助下办理了军训免训，不上早晚自习，酌情协调课程安排等。还给我约了学校的心理咨询中心好多次。</p><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="暮雨" tabindex="-1">暮雨 <a class="header-anchor" href="#暮雨" aria-label="Permalink to &quot;暮雨&quot;">​</a></h3><ul><li>QQ：1903931594</li></ul>',26)]))}const u=e(n,[["render",d]]);export{p as __pageData,u as default};
