import{_ as e,c as a,o as l,aB as r}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"温州大学","description":"","frontmatter":{"title":"温州大学","author":["九猫"],"wordCount":910},"headers":[],"relativePath":"campus/WZU.md","filePath":"campus/WZU.md","lastUpdated":1739159899000}'),n={name:"campus/WZU.md"};function d(i,t,o,c,s,h){return l(),a("div",null,t[0]||(t[0]=[r('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.wzu.edu.cn/" target="_blank" rel="noreferrer">温州大学</a> （Wenzhou University，WZU），由温州市人民政府举办，是浙江省教育厅和温州市人民政府共建高校、“省市共建” 浙江省重点建设高校，博士学位授予单位。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校共有南校区、北校区（两者并称茶山校区）和学院路校区三个校区，大部分学生就读于茶山校区。学校距离火车站 17 公里，公交车总站就在大学城里，经过学校的地铁预计将于 2027 年通车。</p><ul><li>南校区位于<a href="https://ditu.amap.com/place/B0FFF03L6B" target="_blank" rel="noreferrer">浙江省温州市茶山高教园区</a></li><li>北校区位于<a href="https://ditu.amap.com/place/B0241041VS" target="_blank" rel="noreferrer">浙江省温州市茶山高教园区</a></li><li>学院路校区位于<a href="https://ditu.amap.com/place/B02410WW07" target="_blank" rel="noreferrer">浙江省温州市学院中路 276 号</a></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><p>大一新生会在国庆节前后军训，时长两周，强度适中，一般是男女分队训练，在男女比例悬殊的学院会出现男女混合的情况。军训对头发无要求，可以申请跟训。</p><p>大一第一学期的体育课是基础课，男女分开上，这个不及格的话需要上体能加强班。之后的三个学期需要自行选课，供选择的比较多，部分课程男女混合，建议选择太极拳、羽毛球等强度比较低的课。</p><blockquote><p>据说学校还有个保健班，是否可以用易性症申请未知。</p></blockquote><p>校园跑步每学期要求 45 次，每次 2 公里，配速 8 分钟 / 公里。校园跑步在每学期的体育课中占有一定比例。综测的体育分中，校园跑步和体测分数各占一半，跑步跑满 45 次及体测及格就不会影响奖学金的评选。</p><blockquote><p>关于体测是否能用易性症等申请免测暂时未知，笔者会尽快（大概就是这个学期内）给大家答复。</p></blockquote><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><blockquote><p>“温州骑行大学” 到处都是共享单车，不需要担心上课迟到。</p></blockquote><p>学校有五个宿舍区，其中 A 区在南校区内，B 区在北校区内，C、D、E 区在校外，距离学校 1 公里左右，大部分宿舍是六人寝，有一部分是四改六，可能会比较挤。有独立卫浴。学校可以申请两人间，具体条件未知。可以申请校外租房居住。未发现第三卫生间。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校内医院是否有相关医疗服务未知。激素检查等请前往温州康宁医院，还可以开具易性症证明。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><blockquote><p>笔者向班上部分同学出柜过，总体来说比较友善。</p></blockquote><p>未发现对 LGBT + 的不友好现象。</p><h3 id="跨性别分别情况" tabindex="-1">跨性别分别情况 <a class="header-anchor" href="#跨性别分别情况" aria-label="Permalink to &quot;跨性别分别情况&quot;">​</a></h3><p>暂时只有两只 MtF。</p><blockquote><p>欢迎大家来抱团取暖 &gt;&lt;</p></blockquote><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><blockquote><p>学校附近真的超多美食！</p></blockquote><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><ul><li>QQ：3107454772</li></ul>',28)]))}const x=e(n,[["render",d]]);export{u as __pageData,x as default};
