import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"西交利物浦大学","description":"","frontmatter":{"title":"西交利物浦大学","author":["anyuzu99","Catherina"],"wordCount":576},"headers":[],"relativePath":"campus/XJTLU.md","filePath":"campus/XJTLU.md","lastUpdated":1728116039000}'),n={name:"campus/XJTLU.md"};function i(d,t,s,o,h,c){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">部分 ✓</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗（太仓校区有）</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td></tr></tbody></table><h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="https://www.xjtlu.edu.cn" target="_blank" rel="noreferrer">西交利物浦大学</a>（Xi’an Jiaotong-liverpool University，XJTLU）是经中国教育部批准，由西安交通大学和英国利物浦大学合作创立的，具有独立法人资格和鲜明特色的新型国际大学。她是中国目前规模最大的中外合作大学，以理工管起步，强强合作，拥有中华人民共和国学士学位和英国利物浦大学学位授予权。</p><h2 id="地理位置" tabindex="-1">地理位置 <a class="header-anchor" href="#地理位置" aria-label="Permalink to &quot;地理位置&quot;">​</a></h2><p>江苏苏州 <s>苏州的同类浓度很高哦</s></p><ul><li>独墅湖校区（本部）位于 <a href="https://amap.com/place/B0FFHQJBV5" target="_blank" rel="noreferrer">苏州市虎丘区独墅湖高教区仁爱路 111 号</a>，工业园区独墅湖科教创新区，二号线附近，走路稍远，周边有多个商业区。</li><li>太仓校区位于 <a href="https://amap.com/place/B0FFLAQU9Z" target="_blank" rel="noreferrer">苏州市太仓市太仓大道 111 号</a>，公车 1.5 h 到达上海。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><ul><li>军训非常宽松，维持一周，每天半天时间上课，半天时间军训。</li><li>体育课自选，选上好的有点难，但是可能有舞蹈等少有人选。</li></ul><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><ul><li>本部校区 <ul><li>宿舍有独立浴室。</li><li>校外租房：每月 1000（合租）~2000（独租）。</li><li>教学区厕所有隔板和门。</li></ul></li><li>太仓校区 <ul><li>宿舍有独立卫浴。</li><li>宿舍有单人间，双人间，四人间（价格为 1.4w，6k，3k），宿舍空间较小。</li><li>学校附近 2 公里内没有合适的可以租住的房子（写于 2024.9.2）</li></ul></li></ul><h2 id="lgbt-梗概" tabindex="-1">LGBT 梗概 <a class="header-anchor" href="#lgbt-梗概" aria-label="Permalink to &quot;LGBT 梗概&quot;">​</a></h2><p>校内存在 LGBT 社团，校内非常多 LGBT。</p><h2 id="医疗资源情况" tabindex="-1">医疗资源情况 <a class="header-anchor" href="#医疗资源情况" aria-label="Permalink to &quot;医疗资源情况&quot;">​</a></h2><p>医保可直接用于市区所有三甲医院，报销额度约 60%．</p><p>苏州市立医院 <a href="https://mtf.wiki/zh-cn/docs/hrt/zhou-jianying/" target="_blank" rel="noreferrer">周建英</a> 可以开具 HRT 处方与药物。</p><p>苏州广济医院 <a href="https://mtf.wiki/zh-cn/docs/psyco/jiangsu/du-xiangdong/" target="_blank" rel="noreferrer">杜向东</a> 可尝试开证、ADHD 开药，也可前往到南京。</p><h3 id="投稿人" tabindex="-1">投稿人 <a class="header-anchor" href="#投稿人" aria-label="Permalink to &quot;投稿人&quot;">​</a></h3><p><a href="https://yuzu-trans.notion.site/yuzu-trans/yuzu-trans-b68ddbda6d7e47d4a75863977301232b" target="_blank" rel="noreferrer">anyuzu99</a> <a href="https://github.com/anyuzu99" target="_blank" rel="noreferrer">Github</a></p>',18)]))}const g=e(n,[["render",i]]);export{p as __pageData,g as default};
