import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"西安交通大学","description":"","frontmatter":{"title":"西安交通大学","author":["NaiveTomcat"],"wordCount":2015},"headers":[],"relativePath":"campus/XJTU.md","filePath":"campus/XJTU.md","lastUpdated":1739159899000}'),n={name:"campus/XJTU.md"};function i(d,t,o,c,h,s){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">-</td><td style="text-align:center;">依宿舍楼而定 <strong>见下</strong></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td><td style="text-align:center;">个别宿舍楼内有浴室，目前尚未有房间内独立浴室 <strong>见下</strong></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">-</td><td style="text-align:center;">需辅导员 / 书院院务主任提供材料</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">凭写有 “不适合剧烈运动” 的三甲以上医院诊断证明可参加保健班</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">✓</td><td style="text-align:center;">大学生医保，学期内非校医院就诊需校医院转诊单方可报销</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="https://www.xjtu.edu.cn/" target="_blank" rel="noreferrer">西安交通大学</a>（Xi&#39;an Jiaotong University, XJTU），是我国最早兴办、享誉海内外的著名高等学府，是教育部直属重点大学，首批进入国家 “211” 和 “985” 工程建设学校。2017 年入选国家一流大学建设名单 A 类建设高校，2022 年入选国家第二轮 “双一流” 建设高校，8 个学科入选 “双一流” 建设学科，涵盖理、工、医、经、管、文、法、哲、艺、教育、交叉等 11 个学科门类的综合性研究型大学，设有 32 个学院（部、中心）、9 个本科书院和 3 所直属附属医院。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>兴庆校区位于<a href="https://amap.com/place/B001D06LLT" target="_blank" rel="noreferrer">西安市碑林区咸宁西路</a>，目前为非医学及财经类本科生的主要校区，部分专业的研究生也在此校区。本校区在市区内，交通较为便利，附近有多个地铁站，涵盖三号线、五号线和六号线。校区内设有康桥和梧桐两个食堂，学校附近也有较多的饭店。</p></li><li><p>雁塔校区（西安交通大学医学院）位于<a href="https://amap.com/place/B0FFG5C46K" target="_blank" rel="noreferrer">西安市雁塔区雁塔西路</a>，目前为医学专业和经金学院的主要校区。教学区与生活区中间被社会道路（朱雀大街）分割，各设有一个食堂。附近有地铁二号线纬一街站。校区接近西安市目前中心商业区，距离小寨仅一站地铁。</p></li><li><p>创新港校区（中国西部科技创新港）位于<a href="https://amap.com/place/B0GRR5KEB6" target="_blank" rel="noreferrer">西咸新区沣西新城</a>，目前大部分研究生在此校区。校区较为偏僻，面积较大，内有两个地铁站（五号线，创新港站和创新港西站）。食堂等信息笔者暂不详。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>笔者因参赛及休学暂未参加军训，此部分暂时不详。</p><p>已知信息有：无特殊情况对头发长度有要求，计入扣分项；有辅导员或书院院务主任向军事教研室提供说明的情况下可免去此问题。</p><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>本科期间要求修读 4 个学期的体育课，通常在大一大二修完。内容可自选，需在选课平台上抢课。大部分课程男女分开，少数课程（如陆上赛艇）及保健班不分男女。凭三甲以上医院出具的写有 “不适合剧烈运动” 的诊断证明可参加保健班。一般课程中体适能等体能类课程强度较大，球类运动及武术套路主要考察技巧。期末考核均为定量计分，暂无申请降低标准的先例。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测每学年一次，大四体测成绩占 50%，前三学年体测成绩一共占 50%，加权平均，毕业需 50 分以上。</p><p>体测标准按指派性别对应的《国家学生体质健康标准》进行，包括身高、体重、肺活量、坐位体前屈、50 米、立定跳远、引体向上（男）/ 仰卧起坐（女）、1000 米（男）/800 米（女），共计 8 个项目。因病或受伤可申请缓测；患有不适合剧烈运动的重大病症或残疾的可凭校医院开具的诊断证明办理免测手续，计 80 分。</p><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>由于笔者在兴庆校区就读，以下消息如无特殊说明仅适用于兴庆校区。</p><p>宿舍环境一般，大部分宿舍楼四人一间，上床下桌。部分宿舍（已知文治、崇实、励志、仲英）宿舍内有独立卫生间，其余的在每层楼设有公共卫生间。</p><p>根据《西安交通大学兴庆校区及雁塔校区学生公寓服务与管理办法》，可申请调换宿舍床位，需填写《西安交通大学学生公寓调换申请表》，经相关部门研究批准后进行调换。</p><p>可在校外居住，理论上需填写校外住宿申请表并退宿，但如不进行退宿并正常缴纳住宿费用，学校并不会进行在何处居住的核查，因而可不进行相关手续。</p><p>兴庆校区在学生区东西各设有一个浴室，东侧温泉浴室容量较大，有单间和大澡堂；西侧在崇实书院后部，容量较小，仅有单间。部分书院宿舍楼内设有浴室，笔者所在文治书院在 2024 年暑期浴室重新装修后改造为单间浴室。洗浴价格按用水时间计算， <code>0.08</code> 元每分钟</p><p>目前暂未在学校内发现第三卫生间。</p><p>有关创新港住宿方面，收到报告称学校有意将类似情况（Trans）学生安排至集中住宿，且住宿条件属于创新港较好水平。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院为二级乙等医院，可进行激素检查，但需提前预约，且需在规定时间到达校医院进行抽血，大学生医保可报销。目前暂不知晓校医院能否购买 HRT 药物。学校附近多家大型综合性医院，基本均能进行激素检查，已知西京医院和西安交通大学第一附属医院能购买 HRT 药物。</p><p>大学生医保普通门诊报销指定医院为校医院，在外医院就诊报销需转诊单，门诊报销比例 70%，年限额 500 元。</p><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>学校内没有 LGBT 相关社团，且其成立大概率不会被团委通过。大部分同学及老师对 LGBT 友好，但也存在部分反跨魔怔人。学校内有心理咨询中心，但在笔者因精神问题被辅导员向家长建议前往学校心理咨询中心咨询且暗示辅导员可能得知咨询内容的情况下，笔者放弃前往，且对该中心不信任。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>目前已知跨性别总数不多，集中在少年班。根据历史统计数据少年班平均每届出现 1 名 MtF，由于少年班内指派女性数量较少暂未在少年班中发现 FtM。在其余学院专业也有 MtF 和 FtM 的分布。</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>少年班所在的钱学森书院，院务主任已知对跨性别友好，且欢迎少年班学生前往他处聊天。少年班内大部分同学 LGBT 友好。</p><hr><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="naivetomcat" tabindex="-1">NaiveTomcat <a class="header-anchor" href="#naivetomcat" aria-label="Permalink to &quot;NaiveTomcat&quot;">​</a></h3><ul><li>QQ：1204521668</li><li>E-Mail：<a href="mailto:tomdang@naivetomcat.cn" target="_blank" rel="noreferrer">tomdang@naivetomcat.cn</a></li><li>Telegram: <a href="https://t.me/NaiveTomcat" target="_blank" rel="noreferrer">@NaiveTomcat</a></li></ul><h3 id="以及提供消息来源的小伙伴们" tabindex="-1">以及提供消息来源的小伙伴们 <a class="header-anchor" href="#以及提供消息来源的小伙伴们" aria-label="Permalink to &quot;以及提供消息来源的小伙伴们&quot;">​</a></h3>',36)]))}const g=e(n,[["render",i]]);export{x as __pageData,g as default};
