import{_ as e,c as a,o as l,aB as r}from"./chunks/framework.uEqVN41L.js";const m=JSON.parse('{"title":"燕山大学","description":"","frontmatter":{"title":"燕山大学","author":["255doesnotexist"],"wordCount":1042},"headers":[],"relativePath":"campus/YSU.md","filePath":"campus/YSU.md","lastUpdated":1728116039000}'),i={name:"campus/YSU.md"};function n(d,t,s,o,h,c){return l(),a("div",null,t[0]||(t[0]=[r('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">仅中外合办学院、部分研究生</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td></tr><tr><td style="text-align:center;">军训</td><td style="text-align:center;">✓</td></tr><tr><td style="text-align:center;">体测凭医疗材料减免</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td></tr></tbody></table><h2 id="学校名称" tabindex="-1">学校名称 <a class="header-anchor" href="#学校名称" aria-label="Permalink to &quot;学校名称&quot;">​</a></h2><p>燕山大学（YSU）</p><h2 id="校园概况" tabindex="-1">校园概况 <a class="header-anchor" href="#校园概况" aria-label="Permalink to &quot;校园概况&quot;">​</a></h2><ul><li>学费：普通类专业有 5060、5390、10000 元 / 年等不同标准，艺术体育类专业为 8000 元 / 年，中外合作办学专业有 25500、72000 元 / 年等不同标准；住宿费： 800 元 / 年、上床下桌宿舍： 1200 元 / 年。</li><li>没有空调。教学楼、宿舍均没有。但课题室、老师办公室、图书馆有。请去图书馆吹。</li><li>学校很大。有校内共享单车设施、公交可用。</li><li>校内迷路可使用高德地图导航。标出的点基本还是准的。路痴福音。</li><li>门口均设卡需刷脸、学生卡进入。但六号门仅设门卫无门禁。</li><li>关于升学、保研等其他事务可参阅网协留下的 <a href="https://github.com/yuna2017/YSU-Guidebook" target="_blank" rel="noreferrer">燕山大学生存手册</a>。</li><li>校内有 KFC、麦当劳、塔斯汀、瑞幸、蜜雪冰城等。支持无接触点餐。适合不希望说话的社恐。</li><li>如果需要攒钱，心怡等餐厅提供 4 元每顿的一菜一饭。足够充饥。</li></ul><h2 id="地理位置" tabindex="-1">地理位置 <a class="header-anchor" href="#地理位置" aria-label="Permalink to &quot;地理位置&quot;">​</a></h2><ul><li>学校位于<a href="https://www.amap.com/search?query=%E7%87%95%E5%B1%B1%E5%A4%A7%E5%AD%A6&amp;city=130000&amp;geoobj=118.638688%7C39.456936%7C121.040012%7C40.460774&amp;zoom=9.8" target="_blank" rel="noreferrer">中国河北省秦皇岛市海港区河北大街西段 438 号</a></li><li><a href="https://www.ysu.edu.cn/info/2197/2255.htm" target="_blank" rel="noreferrer">校园地图</a>、<a href="https://www.720yun.com/t/3cbj5phaea6?scene_id=10952137" target="_blank" rel="noreferrer">校园全景照片</a></li><li>学校地理位置离海边非常近，适合和<s>小男友</s>关系很好的普通同学去海滩散步。</li></ul><h2 id="transgenders-及其他性少数概况" tabindex="-1">Transgenders 及其他性少数概况 <a class="header-anchor" href="#transgenders-及其他性少数概况" aria-label="Permalink to &quot;Transgenders 及其他性少数概况&quot;">​</a></h2><ul><li>校园内主流氛围未知，似与基本盘类似。在校内音游群名声不好，原因未知。不混圈，因此笔者亦不清楚有无其他 Transgenders。</li><li>校内不存在第三卫生间，但东区旁的茂业购物中心似乎有。</li><li>学校似乎有非正式性少数组织，仅见过相关张贴海报。且很快被撤下。笔者无意也未与其建立过任何联系。</li><li>学校的心理咨询笔者从未去过，无法提供相关信息。</li></ul><h2 id="宿舍概况" tabindex="-1">宿舍概况 <a class="header-anchor" href="#宿舍概况" aria-label="Permalink to &quot;宿舍概况&quot;">​</a></h2><ul><li>本科生宿舍一般为六人间（无独卫），研究生、中外合办分配四人间（是否独卫随机）。</li><li>学校无独立卫浴，需要挤北方公共澡堂。东校区澡堂无隔断，互相可见，第二性征已发育的朋友需要注意。西校区男生澡堂有隔断但无帘。进门右拐至最后一排澡间可见浴帘。可以挂上。而女生澡堂均有隔断、浴帘。</li><li>宿舍无空调。但不计电费。大部分本科生宿舍限电 800W。可能有正在建设的定时断电系统。</li><li>每晚 23:30（±~5min） 至 5:30 校园网用户端设备自动下线所有本科生账户。但周五、周六晚不限制。此限制仅与星期有关，与节假日无关。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><ul><li>军训原 14 天，24 年起扩充至 21 天。内容较松。撑过去就行。</li><li>学校大一至大三有两次体育课选课。可以询问学长选择较轻松的几门课程。</li><li>暂不明确体测成绩是否可用病历免考。</li></ul><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><ul><li>校医院～= 包扎伤口、小感冒医疗处。</li><li>开证、HRT 等医疗资源，秦皇岛本地医院暂不清楚。但学校离北京仅有数小时车程。几十元即可负担往返。去北三相对方便。</li><li>广缘大药房存在戊酸雌二醇。可以购买补佳乐。</li></ul><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><ul><li>学信网信息约在毕业前数个月锁定。</li><li>教务处老师建议在第七学期（大四上）前完成所有身份信息变更并提交相关文件。</li><li><a href="https://jwc.ysu.edu.cn/info/1055/5989.htm" target="_blank" rel="noreferrer">在校本科学生申请修改学籍信息办理流程</a></li><li>更改学信网相关信息的流程正待跑通。</li></ul>',17)]))}const b=e(i,[["render",n]]);export{m as __pageData,b as default};
