import{_ as e,c as l,o as a,aB as i}from"./chunks/framework.uEqVN41L.js";const x=JSON.parse('{"title":"浙大城市学院","description":"","frontmatter":{"title":"浙大城市学院","author":["MossCG"],"wordCount":1019},"headers":[],"relativePath":"campus/ZUCC.md","filePath":"campus/ZUCC.md","lastUpdated":1739159899000}'),r={name:"campus/ZUCC.md"};function n(d,t,o,s,c,h){return a(),l("div",null,t[0]||(t[0]=[i('<h2 id="概况" tabindex="-1">概况 <a class="header-anchor" href="#概况" aria-label="Permalink to &quot;概况&quot;">​</a></h2><p><a href="http://www.zucc.edu.cn" target="_blank" rel="noreferrer">浙大城市学院</a>（Zhejiang University City College，ZUCC）是一所经国家教育部批准，由杭州市人民政府举办的全日制公办普通高等学校。其前身为创建于 1999 年的浙江大学城市学院。</p><blockquote><p>目前正在更名为 Hangzhou City University（HZCU）</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;">看教官，一般不管</td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><p>学校分南北校区，中间就隔了一条马路，校区之间通行有两条地下通道。</p><ul><li>学校位于 <a href="https://amap.com/place/B023B06X4D" target="_blank" rel="noreferrer">浙江省杭州市拱墅区湖州街 51-53 号</a>。</li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><ul><li>军训时间：大一开学（八月底 - 九月初）。</li><li>军训时长：约两周。</li><li>个人建议：如果正处于 HRT 阶段体能不好的话，若无法申请免训等条件，可以通过媒体方面的特长（如视频剪辑，摄影，文稿等）进入宣传队，主要任务会以在办公室里面剪视频或者出去拍摄为主，能轻松很多很多，而且 OMC 的学长都很友善的哦～</li><li>关于免训：未知，但疾病可申请免训或缓训。</li><li>关于头发：对染发抓到可能会扣分，要求戴帽子，但其实把头发收起来或许没影响，看教官要求，并无具体要求。</li></ul><h3 id="关于体育" tabindex="-1">关于体育 <a class="header-anchor" href="#关于体育" aria-label="Permalink to &quot;关于体育&quot;">​</a></h3><ul><li>每学期要求男生 120km，女生 100km 的日常跑，单次记录不少于 3km（男生）/2km（女生）。</li><li>体测要测 1000m/800m 并计入期末成绩。</li><li>不确定是否可以申请免测，如果有医院证明大概率可以。</li></ul><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><h3 id="宿舍情况" tabindex="-1">宿舍情况 <a class="header-anchor" href="#宿舍情况" aria-label="Permalink to &quot;宿舍情况&quot;">​</a></h3><blockquote><p>学校内宿舍正在翻修，大部分已经翻修完了，新的宿舍挺漂亮的～</p></blockquote><h4 id="对于新宿舍楼" tabindex="-1">对于新宿舍楼 <a class="header-anchor" href="#对于新宿舍楼" aria-label="Permalink to &quot;对于新宿舍楼&quot;">​</a></h4><ul><li>四人间，独卫 + 独浴。</li><li>床自带一个衣柜，外面阳台还有一个小一点的柜子，收纳空间还是不错的。</li><li>南校没有开放式阳台，晒衣服得开窗不然会很湿（恼。北校有开放式阳台。</li><li>查寝为周六 / 周日学生查寝，以及周一宿管查寝，基本上就是进来看一眼卫生，不会翻东西（致远楼）。</li><li>宿舍校园网很快（100Mbps 下行 40Mbps 上行不限制多拨，L2TP 协议），接口百兆，但是晚上 11:30 断网，不断电。</li><li>热水插卡使用，一般洗 10 分钟 2 块钱左右，晚 12 点后停热水供应。</li></ul><h4 id="对于老宿舍楼" tabindex="-1">对于老宿舍楼 <a class="header-anchor" href="#对于老宿舍楼" aria-label="Permalink to &quot;对于老宿舍楼&quot;">​</a></h4><ul><li>四人间，独立卫浴但是卫浴在一个地方。</li><li>老楼设施比较老。</li><li>南校老楼没有开放式阳台，晒衣服得开窗，北校老楼有开放式阳台。</li><li>查寝、校园网、热水同上。</li></ul><h3 id="基础设施" tabindex="-1">基础设施 <a class="header-anchor" href="#基础设施" aria-label="Permalink to &quot;基础设施&quot;">​</a></h3><ul><li>南校区基础设施较老，北校区基础设施较新。</li><li>无第三卫生间。</li><li>全校 WiFi 覆盖。</li><li>点外卖方便，部分店铺校内能送。</li><li>电动车需要办理通行证。</li><li>部分专业大一有晚自习，需要打卡，但是可以 skip。</li></ul><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><blockquote><p>待补充，笔者从未去过校医院。</p></blockquote><h2 id="lgbt-友善情况" tabindex="-1">LGBT 友善情况 <a class="header-anchor" href="#lgbt-友善情况" aria-label="Permalink to &quot;LGBT 友善情况&quot;">​</a></h2><p>已知：</p><ul><li>无 LGBT 相关社团。</li><li>发现少量 Trans，但隐藏很深。</li><li>学工办李 ll 老师、骆 yy 老师友跨</li><li>大部分老师不知道跨相关信息</li><li>大部分学生友跨，暂未观察到反跨现象</li><li>挂雷达到处跑毫无问题</li></ul>',26)]))}const b=e(r,[["render",n]]);export{x as __pageData,b as default};
