import{_ as e,c as a,o as r,aB as l}from"./chunks/framework.uEqVN41L.js";const u=JSON.parse('{"title":"中南财经政法大学","description":"","frontmatter":{"title":"中南财经政法大学","author":"クリーム","wordCount":1612},"headers":[],"relativePath":"campus/ZUEL.md","filePath":"campus/ZUEL.md","lastUpdated":1739159899000}'),n={name:"campus/ZUEL.md"};function d(i,t,o,c,s,h){return r(),a("div",null,t[0]||(t[0]=[l('<table tabindex="0"><thead><tr><th style="text-align:center;">项目</th><th style="text-align:center;">情况</th><th style="text-align:center;">备注</th></tr></thead><tbody><tr><td style="text-align:center;">宿舍独卫</td><td style="text-align:center;">✓</td><td style="text-align:center;">个别楼除外</td></tr><tr><td style="text-align:center;">宿舍独浴</td><td style="text-align:center;">-</td><td style="text-align:center;">部分有</td></tr><tr><td style="text-align:center;">第三卫生间</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训协商减免</td><td style="text-align:center;">？</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">军训凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;">限制不严</td></tr><tr><td style="text-align:center;">军训不管头发</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育协商减免</td><td style="text-align:center;">?</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">体育凭医疗材料减免</td><td style="text-align:center;">✓</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">医保使用</td><td style="text-align:center;">-</td><td style="text-align:center;">需求不同使用情况也不同</td></tr><tr><td style="text-align:center;">校医院服务</td><td style="text-align:center;">✗</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p><a href="http://www.zuel.edu.cn/" target="_blank" rel="noreferrer">中南财经政法大学</a> (Zhongnan University of Economics and Law , ZUEL) ，中南财经政法大学是教育部直属的全国重点大学，是国家 “211 工程” 和 “985 工程优势学科创新平台” 重点建设高校，是国家 “双一流” 建设高校，是教育部、财政部和湖北省共建高校。学校形成了以经济学、法学、管理学为主干，哲学、教育学、文学、史学、理学、工学、艺术学和交叉学科等 11 个学科门类协调发展的学科体系。现有国家重点学科 4 个，国家重点（培育）学科 1 个。法学学科入选国家 “双一流” 建设学科名单。共有首义、南湖两个校区，另有石咀校区建设中。</p><h2 id="地理位置及交通" tabindex="-1">地理位置及交通 <a class="header-anchor" href="#地理位置及交通" aria-label="Permalink to &quot;地理位置及交通&quot;">​</a></h2><ul><li><p>南湖校区位于<a href="https://surl.amap.com/5oRMT1u018ftZ0" target="_blank" rel="noreferrer">武汉市江夏区南湖大道 182 号</a>，是主要校区，附近有公交站无地铁站，交通稍有不便。但附近有小吃街（比较出名），基本能满足除就医外的所有需求。</p></li><li><p>首义校区位于<a href="https://surl.amap.com/5rirLF2ohge6" target="_blank" rel="noreferrer">武昌区武珞路 114 号</a>，是除法学院和中韩新媒体学院外所有学生大一居住地（一般大二前需要搬校区），近公交、地铁、火车站，交通极便利。</p></li></ul><h2 id="军训与体育" tabindex="-1">军训与体育 <a class="header-anchor" href="#军训与体育" aria-label="Permalink to &quot;军训与体育&quot;">​</a></h2><h3 id="军训" tabindex="-1">军训 <a class="header-anchor" href="#军训" aria-label="Permalink to &quot;军训&quot;">​</a></h3><p>军训一般三周，不要求剪头发，免训难度未知，半训很简单（只要有诊断证明即可，前往救护点开假条，隔一段时间需要重开一次），半训作息和军训相同，但不需训练，在阴凉处静坐即可。</p><blockquote><p>另有传闻首义军训会更加严格。</p></blockquote><h3 id="体育课" tabindex="-1">体育课 <a class="header-anchor" href="#体育课" aria-label="Permalink to &quot;体育课&quot;">​</a></h3><p>大一上半年集体学习太极拳，压力不大。但体育课考核每年需要跑一次 3000m 和跳绳。大多数课程男女不分。另外有校园跑任务需要完成。<br> 有三甲医院不适运动病历或残疾证可申请体育保健班，强度约等于零且无需进行前述考核（包括校园跑）。</p><h4 id="体测" tabindex="-1">体测 <a class="header-anchor" href="#体测" aria-label="Permalink to &quot;体测&quot;">​</a></h4><p>体测和毕业挂钩，需要 (前三年平均成绩<em> 50%+ 大四成绩</em> 50%)&gt;=50，方可毕业。另大一入学体测。体测全程机器测试，每人只有一次机会。<br> 体测免测分三类，跨性别貌似只能申请 C 类（50 分，不可保研评优评奖学金）。需要亲自到学院盖章，后统一收集交其他部门审核。</p><blockquote><p>由于校医院经常抽风，申请有被打回来的可能。</p></blockquote><h2 id="宿舍情况与基础设施" tabindex="-1">宿舍情况与基础设施 <a class="header-anchor" href="#宿舍情况与基础设施" aria-label="Permalink to &quot;宿舍情况与基础设施&quot;">​</a></h2><p>宿舍大部分四人间、上床下桌，首义少部分六人间但是两房一厅。有独卫，多数没有独浴（即使有，环境也较差，只是在卫生间上面加一个花洒）。可以调寝，不可单人居住，可以校外租房。2024.9 之前均不查寝，11:30 闭寝，但是可以叫醒宿管登记后进入。不断电，有限电。2024.10 由于新换校长，开始不规律查寝，对出校管理也比较严格，不知会持续多久。<br> 全校无第三卫生间。<br> 电力系统极差，极端高温时经常发生大规模停电和空调失灵。</p><h2 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h2><p>校医院条件较差，只能做肝功、肾功等基础检查（一周只有两天，要在 9 点之前挂号请医生开具）。<br> 关于医保，校医院医保自动报销。然而校医院只能处理简单疾病，而且有时由于各种原因本来可以做的检验检查都做不了。校外就医需要校医开具转诊单，留好票据，每季度统一报销一次。<br> 激素六项未知</p><blockquote><p>笔者两次开具六项均失败，医生回答 “因为是自费所以医院没准备这个项目”。但也有顺性别女性在妇科开出激素六项的传闻。<br> 校医经常性拒绝开具转诊单，询问理由则答复 “不开”、“没有这回事”、“开不了”，即使校医院没有检查或治疗条件。自己去对口医院就医后回校医院补转诊单同样会遭到拒绝。因此转诊查六项再报销很难实现。（以上均是南湖校区情况，首义未知，欢迎补充）<br> 由于校医院的种种抽风行为，笔者自入学以来所花医药费已经超过了学费和住宿费的总和，因此极不建议身体情况不佳者报考此学校。</p></blockquote><h2 id="lgbt-友善梗概" tabindex="-1">LGBT 友善梗概 <a class="header-anchor" href="#lgbt-友善梗概" aria-label="Permalink to &quot;LGBT 友善梗概&quot;">​</a></h2><p>学校整体比较开放，L、G 都很多，未见学生对 LGBT 有明显歧视，只有少数教师在课上发表过反 LGBT 言论，辅导员普遍比较年轻，大部分友跨或中立。未见 LGBT 相关社团。心理咨询也不难约，笔者所见咨询师都很友善，但友跨程度未知。如果咨询师认为问题比较严重会建议咨询者前往精神科就医。</p><h3 id="跨性别分布情况" tabindex="-1">跨性别分布情况 <a class="header-anchor" href="#跨性别分布情况" aria-label="Permalink to &quot;跨性别分布情况&quot;">​</a></h3><p>已知动漫社有几只 MtF，其余未知。</p><h3 id="院系探路" tabindex="-1">院系探路 <a class="header-anchor" href="#院系探路" aria-label="Permalink to &quot;院系探路&quot;">​</a></h3><p>法学院：亚洲最大法学院（自封），人多所以素质参差不齐，但是没见过明显的反跨团体。大都很卷，也没什么空关注别人。</p><h2 id="投稿人联系方式" tabindex="-1">投稿人联系方式 <a class="header-anchor" href="#投稿人联系方式" aria-label="Permalink to &quot;投稿人联系方式&quot;">​</a></h2><h3 id="贡献者姓名" tabindex="-1">贡献者姓名 <a class="header-anchor" href="#贡献者姓名" aria-label="Permalink to &quot;贡献者姓名&quot;">​</a></h3><p>クリーム</p><ul><li>E-Mail：<a href="mailto:cream409445@gmail.com" target="_blank" rel="noreferrer">cream409445@gmail.com</a></li></ul>',29)]))}const p=e(n,[["render",d]]);export{u as __pageData,p as default};
